css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("2-ch.ru") {
html, body {
  font-size:10pt !important;
  background:#EDDAD2 !important;
  color:#000000 !important;
}
* {
  font-family: "Trebuchet MS", Tahoma, Verdana, Arial, sans-serif !important;
  font-size: 10pt !important;
}
input, textarea {
  background-color: #E6CBC0 !important;
  border: 1px solid #CA927B !important;
}
a {
  background:inherit !important;
  color:#34345C !important;
}
a:visited {
  background:inherit !important;
  color:#34345C !important;
}
a:hover {
  color:#DD0000 !important;
  background:inherit !important;
}
a.quotelink {
  background:inherit !important;
  color:#DD0000 !important;
}
.logo {
  clear:both !important;
  text-align:center !important;
  background:inherit !important;
  font-size:24pt !important;
  color:#AF0A0F !important;
  width:100% !important;
}
.postarea {
  background:inherit !important;
}
.postblock {
  background: #D9AF9E !important;
  border: 1px solid #CA927B !important;
  color: black !important;
  font-weight: bold !important;
  padding: 2px 5px 2px 5px !important;
}
.footer {
  text-align:center !important;
  font-size:12px !important;
}
.unkfunc {
	background:inert !important;
	color:#789922 !important;
}
.filesize {
	text-decoration:none !important;
}
.filetitle {
	background:inherit !important;
	color:#0F0C5D !important;
	font-weight:800 !important;
}
.postername {
  background:inherit !important;
  font-size:11pt !important;
  color:#117743 !important;
  font-weight: bold !important;
}
.postertrip {
	background:inherit !important;
	color:#228854 !important;
}
.oldpost {
  background:inherit !important;
  color:#0F0C5D !important;
  font-weight:800 !important;
}
.omittedposts {
  background:inherit !important;
  color:#070707 !important;
}
.reply {
	background:#D9AF9E !important;
	color:#000000 !important;
	border:1px solid #CA927B !important;
}
.replyhl {
	background:#D6BAD0 !important;
	color:#000000 !important;
}
.doubledash {
 	vertical-align:top !important;
	clear:both !important;
	float:left !important;
}
.replytitle {
	background:inherit !important;
	font-size:18px !important;
  color:#0F0C5D !important;
  font-weight:800 !important;
}
.commentpostername {
  background:inherit !important;
  font-size:11pt !important;
  color:#117743 !important;
  font-weight: bold !important;
}
a.quotejs {
  color:#000000 !important;
	text-decoration: none !important;
}
a.quotejs:hover {
  font-weight:bold !important;
}

.adminbar {
  text-align: center !important;
}
p.spoiler > span.warning {
	color: #FF6600 !important;
	font-weight: bolder !important;
}
span.spoiler {
	background: #CA927B !important;
	color: #CA927B !important;
}
span.spoiler:hover {
	color: #000000 !important;
}
}
