css:
/* AGENT_SHEET */
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* Main window */
#main-window { background-color: #222 !important; }

/* Toolbars */
#navigator-toolbox,
#navigator-toolbox #toolbar-menubar,
#navigator-toolbox #nav-bar,
#PersonalToolbar,
#navigator-toolbox,
#PersonalToolbar > * {background: #222 !important;
-moz-border-bottom-colors: #333 !important;}

/* Menu Bar */
.menubar-text {/*color: white !important;*/}
#main-menubar menu
{background: #222 !important; color: white !important;}

/*border: none !important;*/


/*#main-menubar menu *{background: #222 !important; color: white !important;} 
*/

/*.menubar-text:hover,
#main-menubar menu:hover,
#main-menubar menu *:hover
{color:#2674C1!important;
opacity: .5 !important;}*/

/* Bookmarks Bar */
#bookmarksBarContent{background: #222 !important;}
.toolbarbutton-text:not(:hover) {color: white;}
.toolbarbutton-text:hover,toolbarbutton.bookmark-item:hover,toolbarbutton.bookmark-item:active,
toolbarbutton.bookmark-item:hover
{color:#2674C1!important;
opacity: .5 !important;}


/* Url bar */

.autocomplete-history-dropmarker
{ list-style-image: none !important; 
margin-right: 4px !important; 
background: transparent url(data:image/vnd.microsoft.icon;base64,) center no-repeat !important; }

#PopupAutoCompleteRichResult richlistbox scrollbar
{background: black !important;
color: white !important;}

#PopupAutoCompleteRichResult richlistbox, 
#PopupAutoCompleteRichResult, 
#PopupAutoComplete
{background: #222 !important;
color: white !important;
border: none !important;}

#urlbar, #MMSearchTBTextbox
{background: #444 !important;
color: white !important;}



/* Searchbar */

#searchbar {opacity:.5!important;}

#search-container {background: #222 !important;}

#search-container > #searchbar .searchbar-textbox      

#search-container:focus, #search-container:hover
  {width: auto !important;}

#search-container:hover > #searchbar .searchbar-textbox,
#search-container > #searchbar .searchbar-textbox[focused="true"]
{}
/*#searchbar .search-go-button, #searchbar .searchbar-dropmarker-image{}*/





/* Statusbar */

#status-bar, #browser-bottombox > *, #statusbar-display, #browser-bottombox,
#statusbar-display[label*="."], #statusbar-display[label*="."] ~ *,
#statusbar-display, statusbarpanel
{background: #222 !important;
color: white !important;
border: none !important;}

statusbarpanel > progressmeter
{opacity: .8 !important;}


/* Tabs */

@-moz-document url("chrome://browser/content/browser.xul"){

.tabbrowser-strip .tabbrowser-tabs
{margin: -4px 0px -4px 0px !important;}

.tabbrowser-strip
{margin: 0px 0px 0px -3px !important;
background: black !important;}

tab
{background: black !important;
-moz-appearance: none !important;}

.tab-text{color: rgb(50,50,250) !important;
opacity: 1 !important;}

.tabbrowser-tab[selected="true"] {opacity: .6 !important;}
.tabbrowser-tab:hover[selected="true"] {opacity: .7 !important;}
.tabbrowser-tab[selected="false"] {opacity: .4 !important;}
.tabbrowser-tab:hover[selected="false"] {opacity: .5 !important;}

}

/* Side bar */
#sidebar-splitter{background: #323232 !important;
-moz-border-top-colors: #333 !important; 
-moz-border-bottom-colors: #333 !important;
-moz-border-left-colors: #333 !important;
-moz-border-right-colors: #333 !important; }
