css:
@namespace url(http://www.w3.org/1999/xhtml);

/*
Webtrh vB4 oldschool hack
autor je Jakub Stacho
licence je cc-by-sa
verze asi tak 23
*/

@-moz-document domain("webtrh.cz") {

/* schovani a osetreni ikonek pod prispevkem */
span.postlinking a.blog, span.postlinking span.seperator, span.postcontrols a.quickreply, span.postcontrols a.multiquote, span.postcontrols span.seperator {display: none !important;}
span.postcontrols {width: 290px; text-align: right;}
span.postlinking {width: 180px;}

/* fullscreen */
html {background-color: white !important;}
body {margin: 0 !important;}
.above_body {-moz-border-radius: 0 !important; -webkit-border-radius: 0 !important;}

/* schovani pro mne zbytecnych boxu */
div#thread_info, div.navlinks, div#threadpagestats, div#pagetitle, h2.title, div#wgo, div.below_body, div#postpagestats_above, li#displaymodes, li#searchthread, li#post_imodsel, li#thread_imodsel, div#pagination_top a.popupctrl, div.remove_ignore, .postbitignored .floatcontainer {display: none !important}

/* deleted nebo ignored */
.postbitignored h2.title, .postbitdeleted h2.title {display: block !important; font-weight: normal !important; font-size: 13px !important}

/* zmenseni hlavicky pro male obrazovky */
a.logo-image img {height: 25px; position: relative; top: 3px; left: 14px;}
div.above_body:hover li.selected ul.floatcontainer li {visibility: visible;}

/* sidebar vpravo (ale nepujde schovat) */
#sidebar_container.sidebarleft {float: right !important; position: absolute; right: 20px;}
#sidebar_button {display: none !important;}
body #content_container {float: right; margin-left: 0 !important;}
#content_container #content {margin-left: 0 !important; margin-right: 210px !important;}
div#pagination_top {position: relative !important; right: 210px !important; top: 15px}
div#above_threadlist div.threadpagenav {position: relative !important; right: 210px !important; bottom: 5px}

/* roztahnuti ankety */
div#pollinfo {margin-left: 0 !important; min-width: 950px;}

/* nejake detaily */
div.forumrow ul.forumactionlinks {visibility: hidden}
a#newreplylink_top {position: relative; top: 6px;}
h2.posttitle {padding-left: 0 !important;}
div.blog div#content {margin-left: 0px !important; padding-right: 80px !important;}
div.blog #sidebar_container {position: absolute; right: 20px;}

/* odkazy */
table a, .userinfo a, .navbit a, .threadbit a, .blogentrybit a, .forumbit_post a, .profile_content a, .blockbody a, .blogitems a, .postcontent a {color: #055ba1 !important;}
.postrow a {text-decoration: underline !important;}
}
