css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
url-prefix(http://boards.4chan.org/),
url-prefix(http://zip.4chan.org/),
url-prefix(http://cgi.4chan.org/),
url-prefix(http://img.4chan.org/),
url-prefix(http://rs.4chan.org/),
url-prefix(http://pawsru.org/fc/),
url-prefix(http://pawsru.org/m/),
url-prefix(http://pawsru.org/f/),
url-prefix(http://pawsru.org/c/),
url-prefix(http://pawsru.org/dis/),
url-prefix(http://pawsru.org/paws/),
url-prefix(http://pawsru.org/ar/),
url-prefix(http://pawsru.org/flash/)


{

/*Colors tables  bg etc*/

* {
font-family:Calibri,Tahoma, Arial, sans-serif !important;
font-size:15px !important;
-moz-appearance: none !important
}

.delform {
color:#FFFFFF !important;
}

.postarea {
padding: 0px !important;
opacity: .90 !important;  
}

.pages {
background-color:transparent !important;
}

span.plus {
color:#FFFFFF !important;
}

.hr {
color:#FFFFFF !important;
}

.omittedposts{
color:#FFFFFF !important;
}

blockquote {
color:#FFFFFF !important;
max-width:900px !important;
}

table {
color:#FFFFFF !important;
}

.logo font b span{
color:#FFFFFF !important;
font-size: 200% !important;
}

#twi_table_div{
border:none !important
}

.filetitle {
color:#FFFFFF !important;
}

textarea{
height:5em !important;
width:25.28em !important;
}

img.thumb,img[src*="/thumb/"] {
background:transparent !important;
border:solid .5px #FFFFFF !important;
-moz-border-radius:0px !important;
opacity:.99 !important;
z-index:3 !important;
}

html,body {
font-size:20px !important;
background-color:#000000 !important;
background-image:url('http://img30.imageshack.us/img30/4812/cassandraf.png') !important;
background-position: right bottom !important;
background-repeat:no-repeat !important;
background-attachment:fixed !important;
color:#FFFFFF !important;
}

.replymode {
background-color:transparent !important;
color:#FFFFFF !important;
}

th {
background-color:#333333 !important;
opacity:.90 !important;
-moz-border-radius:10px !important;
}


tr {
color:#FFFFFF !important;
}

*[id="footer"]  {
-moz-border-radius:0px !important;opacity:.90 !important;background-color:#333333 !important;color:#FFFFFF !important;
border:solid .5px #FFFFFF !important;padding: 10px 20px 16px 20px !important
}

.unkfunc{
color:#FFFFFF !important;
}

#bod1 {
background-color:transparent !important;
}

td input, td textarea{
-moz-border-radius:5.5px !important;
}

[class*="postername"] {
background-color:transparent !important; 
color:#FFFFFF !important
}

[class*="postertrip"] {
color:rgb(0,160,100) !important
}

[class*="abbr"]{
color:#FFFFFF !important
}

div {
color:#666666 !important;
}

* [class="boxcontent"]{
opacity:.90 !important;background-color:#a11e28 !important;color:#FFFFFF !important;
}

* [id="ft"]{
display:none !important;
}

* [class="boxbar"]{
background-color:#a11e28 !important;
}

* [id="filter-container"]{
display:none !important;
}

* [id="options-container"]{
display:none !important;
}

input[type="file"] {
background-color:transparent !important;
}

span [title='']{
color:#FFFFFF !important;
}

sup{
color:#FFFFFF !important;
}

.postarea .postblock{
-moz-border-radius:0px /*2px 0 0 2px*/ !important;
border:solid 0px #FFFFFF  !important;
border-right-style: dashed !important;
border:solid 0px #FFFFFF !important;
text-align:center !important;
background-color:transparent!important;
color:#FFFFFF !important;
}

.postarea:hover .postblock:hover{font-weight:bold !important;color:#FFFFFF !important;}

/* hides ads */

a[target=_top]{display:none !important;}

div.logo img{display:none !important;}

iframe{display:none !important;}

div[style*="text-align: center"]{display:none !important;}

img[style*="border:"]{display:none !important;}

div[id="ad"]{display:none !important;}

a[href="http://adultfriendfinder.com/go/p61140"]{display:none !important;}


/* hr */

hr{display:none !important;}

form hr{display:block !important;}

hr{height:0px !important;border:none !important;background:#999 !important;}


/* rounded corners */

td.reply {-moz-border-radius:0px !important;opacity:.90 !important;background-color:#333333 !important;color:#FFFFFF !important;
border:solid .5px #FFFFFF !important;padding: 0 20px 16px 0 !important}

td.replyhl {-moz-border-radius:0px !important;opacity:.90 !important;background-color:#333333 !important;color:#FFFFFF !important;
border:solid .5px #FFFFFF !important;padding: 0 20px 16px 0 !important}


/* links */

a {text-decoration:none !important;color:#FFFFFF !important;}

*[class*="poster"],*[class*="linkmail"],*[class*="replytitle"],*[class*="filetitle"],*[class*="omittedposts"],*[class*="oldpost"] { color: #FFFFFF !important; border-width: 0px !important; }

*[class*="doubledash"] { color: #FFFFFF !important; }

*[class*="unkfunc"],*[class*="quotelink"],*[class*="reflink"],*[class*="quotejs"] { color: #FFFFFF !important; }

*[target="_blank"]{ color: #FFFFFF !important; }

*[class*="abbr"] { color: #FFFFFF !important; }

a.quotejs:hover, a:hover{font-weight:bold !important;color:#FFFFFF !important;}

b {color:#FFFFFF !important;}

b.quotejs:hover, b:hover{font-weight:bold !important;color:#FFFFFF !important;}

/* removes ugly borders on page overview [0,1,2...] */

table,td{border:none !important;}

td.rules{display:none !important;}



/* logo */

img + br {display:none !important;}


/* blurry thumbnails fix */

img[md5] {

height: auto !important;

width: auto !important;

}
}
