css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("digital.iqads.ro") {

body{font-size:0.9375em!important;line-height:1.25em!important}
#article>a{font-size:inherit!important;color:#1e73a6!important;text-decoration:underline!important}
#article>a:hover,#article>a:focus{color:#145074!important}
#wrap{width:auto!important}
.ToolbarContMoney,
#leaderboard,
.iqads_logos,
#footer>center,
#f5-footer,
#ado.bbb,
table[width="300"],
#widgetMoney,
img[src*="http://realitatero.adocean.pl"],
#ad_flash,
#left object,
#right object
{display:none!important}
#header{height:auto!important}
#logo{top:0!important}
#menu{float:none!important;position:static!important}

#left{position:absolute!important;margin:0!important}
#right{position:absolute!important;right:0!important;top:150px!important}
#content{min-width:460px!important;margin:0!important;width:auto!important;padding:0 0 0 170px!important}
#left+#content{margin:0 180px 0 0!important}
#article{margin:0 1%!important;float:none!important;width:auto!important;font:inherit!important/*-moz-column-count:3;-moz-column-gap:2em*/}
#article #content-right{padding-top:10px!important;margin-left:15%!important}

h1.title{margin:10px 0 15px!important}
.boxcorner-lt,.boxcorner2-lt,
.boxcorner-rt,.boxcorner2-rt,
.boxcorner-rb,.boxcorner2-rb,
.boxcorner-lb,.boxcorner2-lb{background:none!important}
.boxcorner{-moz-border-radius:10px!important}

#content-left{width:auto!important;margin-right:160px!important}
#content-left+#content-right{margin-left:-150px!important}
#content-left .box-head{float:none!important}
.box-head h1,.box-head h1 a{font:bold 16px/22px arial,sans-serif !important}
#content-left .boxcorner-body{display:inline-block!important;vertical-align:top;min-width:14em;width:23%;margin-right:2%}
#content-left .boxcorner-body .nlink1{display:block;margin:0 0 -10px!important}
#content-left .boxcorner-body img.mr5{margin:0 5% 0 0!important}

.nlink1 {margin:0 0 -10px!important;font-size:inherit!important}
.link1{font-size:inherit!important}
.boxcorner-body .nlink1{margin:0!important}
.nlink2{line-height:normal}

}
