css:
@-moz-document domain("www.linux.org.ru"), url-prefix('http://www.linux.org.ru/') {
html,body{background-color:#D9DAC6 !important;}
body{color:#00000d !important;}
a{color:#0000ad !important;}
#hd h1{color:#fff !important;}
#hd .menu .primary a{color:#3F77FF !important;}
#hd .menu .secondary a{color:#3F77FF !important;}
#bd h2 *{color:#0000AD !important;}
#bd .tags a{color:#3F77FF !important;}
#bd .sign a{color:#0000ad !important; text-decoration: none !important;}
#bd .sign a:hover{color:#0080C0 !important; text-decoration: underline !important;}
#bd .sign a:visited{color:#606090 !important;}
#bd .column .boxlet{background-color:inherit !important;}
#bd .column .boxlet h2{background:#797F4F !important;}
#bd .forum table thead{background-color:#C7CBB1 !important;}
#bd .forum table tbody td{border-color:#555753 !important;}
#bd .forum table th{border-color:#555753 !important;}
#bd .forum table tbody td *{color:#0000ad !important;}
.messages .msg{background:#C7CBB1 !important;}
#bd a:visited{color:#606090 !important;}
#bd .forum table tbody td a:visited{color:#babdb6 !important;}
#sitetitle{color:#464A3C;text-decoration:none !important;}
#navPath a:visited{color:#729FCF !important;}
#whois_userpic{background:#C7CBB1 !important;}

.dw-feed-items {
background-color: #D9DAC6 !important;
}
.dw-feed-title-box {
color: #00000D !important;
background-color: #D9DAC6 !important;
}

#bd .forum table tbody tr:hover {
background-color: #C7CBB1 !important;
}

#bd .forum table tbody tr td a:visited {
color: #4F51FF !important;
}

pre {
background-color: #7C7C7C !important;
}

pre .shebang, pre .comment, pre .template_comment, pre .javadoc{
color: ##95FF8F !important;
}

}
