/*
 * Iconbuffet - Restyled
 *
 * Author      : THiC
 * Created     : June 30th, 2007
 * Updated     : August 4th, 2007
 * Version     : 1.4
 * Website     : http://stumbl.be
 * Description : Adds a more stylish background to the site
 *             : (includes all subdomains)
 * Usage       : Use with Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 */


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.iconbuffet), url-prefix(http://www.iconbuffet)
{

body {
	padding: 0px !important;
	margin: 0px !important;
	font-family: "Lucida Grande", "Verdana", sans-serif !important;
	background: #122738 url('http://ll50cc.googlepages.com/wallpapersn2.jpg') repeat-x !important;
	font-size: small !important;
}

#header {
	position: relative !important;
	display: block !important;
	margin: 0 auto !important;
	height: 80px !important;
	background: black url(http://ll50cc.googlepages.com/cornermod.gif) right top no-repeat !important;
}

#footer .copywrite {
	display: block !important;
	clear: left !important;
	line-height: 30px !important;
	padding: 0 0 0 20px !important;
	margin: 0 !important;
	color: #8b8b8b !important;
	background: black url(http://ll50cc.googlepages.com/footer-corners2.gif) bottom left no-repeat !important;
}

.friends li a img {
	padding: 1px !important;
	border: 2px solid #ddd !important;
}
.friends li a:hover img {
	border: 2px dashed #999 !important;
}

.thread blockquote {
  background: #ccdacc !important;
  padding: 8px 10px !important;
  color:#666 !important;
  font-style:italic !important;
  font-size:small !important;
  border: 3px #adbaad solid !important;
  }

.blog-forum table tr.sticky {
	background: #e4ffe0 !important;
}
.blog-forum table tr.sticky:hover {
	background: #d2dee3 !important ;
}

/* added on July 8th */

.comment {
	position: relative;
	font-size: 11px;
	margin-bottom: 12px;
	border: 5px solid #f0ebc6 !important;
        background: #fffbdc !important;
}
.comment .author {
	position: relative;
	padding: 0;
	font-size: 12px;
	padding: 5px 5px 0px 10px !important;
	margin-bottom: 8px;
	overflow: hidden;
	color: #918949;
	background: #fffdef;
}
.comment.whisper {
	border: 5px solid #adbaad !important;
        background: #ccdacc !important;
}
.comment.whisper .author {
	color: #58925f;
	background: #ccdacc !important;
}
/* added on July 29th */

.comment blockquote {
  background: #fffdef !important;
  padding: 8px 10px !important;
  margin-left: 60px !important;
  margin-right: 30px !important;
  margin-bottom: 15px !important;
  color:#666 !important;
  font-style:italic !important;
  font-size:small !important;
  border: 3px #adbaad solid !important;
  }

.comment.whisper blockquote {
  background: #fffdef !important;
  padding: 8px 10px !important;
  margin-left: 60px !important;
  margin-right: 30px !important;
  margin-bottom: 15px !important;
  color:#666 !important;
  font-style:italic !important;
  font-size:small !important;
  border: 3px #adbaad solid !important;
  }
.blog-forum table tr:hover {
	background: #d2dee3 !important ;
}
}
