css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(crunchyroll.com) {
body { background-color: #486060 !important; }

td { background-color: #e6cfd3 !important; }

#logo { display: none !important; }

/* Secondary Text Color */
table tr td { color: #A35467 !important; }

span { color: #BF6095  !important; }

#header { color: #660044 !important; }

table td th { 
background-color: #660044 !important;
color: #A6A6A6 !important;
}

* { font-family: Arial !important; }

img { border: 0px !important; }

a {
color: #C07878 !important;
font-weight: bold !important;
}

a:hover { color: #eea9b8 !important; }

img.imgborder { border: 1px solid #333333 !important; }

.cr-points {
color:#827839 !important; 
font-weight: bold !important;
}

.badge-count {
color: #FFFFFF !important; 
font-weight:bold !important;
}

.online-status {
color:#e6cfd3 !important; 
font-weight:bold !important;
font-size:11px !important;
}

.idle-status {
color:#80596F !important; 
font-weight:bold !important;
font-size:11px !important;
}

.offline-status {
color:#660044 !important; 
font-size:11px !important;
}

.widemainpage { width:100% !important; }

.fullscreenpage { 
width: 100% !important; 
height: 100% !important;
}

.mainpage { 
margin: auto !important; 
width: 935px !important; 
height: 100% !important; 
border-collapse: collapse !important; 
text-align: center !important;
}

.dimtext {
/* Small Text */
color: #A51F3A !important; 
}

#searchbox {  
height:20px !important;  
width:150px !important;  
font-size:14px !important;
}

#searchbtn {  
padding: 2px !important;  
margin: 2px !important;  
cursor:pointer !important;  
height: 22px !important;  
width:60px !important;  
font-size:14px !important;
background-color: #80596F !important;
color: #FFFFFF !important;
border: 1px solid #000000 !important;
}

#logo:hover img { opacity: 1 !important; }

img.dim { opacity: 1 !important; }

.paginator {
/* page links */  
border: 1px solid #FFFFFF !important;  
padding:4px !important;  
font-size: 20px !important;  
color: #E6CFDB !important;  
font-weight:normal !important;
}

.paginator-selected {  
border:1px solid #CCCCCC !important;  
padding: 4px !important;  
font-size:20px !important;  
font-weight: normal !important;  
color: #E6CFDB !important;  
background-color: #AF7B98 !important;
}

.paginator-disabled {  
border:1px solid #E6CFDB !important;  
padding:4px !important;  
font-size:18px !important;  
color: #FCDFFF !important;  
font-weight:normal !important;  
background-color:#E6CFDB !important;
}

a.paginator {  
text-decoration: none !important;  
background-color: #80596F !important;
}

a.paginator:hover {  
background-color:#FCDFFF !important;  
color:#000000 !important;
}

.paginator-sm {  
border:1px solid #7f4e52 !important;  
background-color: #7f4e52 !important;
padding:2px !important;  
font-size:10px !important;  
color: #E6CFDB !important;  
font-weight:normal !important;
}

.paginator-sm-selected {  
border:1px solid #E6CFDB !important;  
padding:2px !important;  
font-size:10px !important;  
font-weight:bold !important;  
color:#035893 !important;  
background-color:#e6cfd3 !important;
}

.paginator-sm-disabled {  
border:1px solid #660044 !important;  
padding:2px !important;  
font-size:10px !important;  
color: #660044 !important;  
font-weight:normal !important;  
background-color:#80596F !important;
}

a.paginator-sm {  
text-decoration:none !important;  
background-color:#035893 !important;
}

a.paginator-sm:hover {  
background-color:#FCDFFF !important;  
color:#035893 !important;
}

.msg { 
/* help out crunchyroll msg */
color: #7D2252 !important; 
border: 3px solid #7D2252 !important;
}

.tablelist_shadow { 
/* unknown */
background-color: #333333 !important;
border: 2px solid #A6A6A6 !important;
}

.boxcontents { border: 0px solid #8b3a62 !important; }

.boxtitle, .boxtitle *{ 
/* I.E. Latest & New Media */ 
color: #A6A6A6 !important;  
background-color: #8b3a62 !important;
}

div.button { background-color: #7f4e52 !important; }

.button a { 
color: #FFFFFF !important;  
text-decoration: none !important;  
font-weight: bold !important;
}

.button a:hover { color: #151B54!important; }

b.btntop, b.btnbottom {
display: block !important;
background-color: #151B54 !important;
}

b.btntop b, b.btnbottom b {
display: block !important; 
height: 1px !important; 
overflow: hidden !important; 
background-color: #C48793!important;
}

b.btnc1 {
/* don't touch */ 
margin: 0 5px; 
}

b.btnc2 {
/* don't touch */ 
margin: 0 3px; 
}

b.btnc3 { 
/* don't touch */
margin: 0 2px; 
}

b.btntop b.btnc4, b.btnbottom b.btnc4 {
/* don't touch */
margin: 0 1px; 
height: 2px;
}

img.thumbnail {  
border-style: solid !important;  
border-color: #486060 !important;
}

img.thumbnail:hover { opacity: 1 !important; }

a.thumbnail:hover img { ; }

img.user-mug { border: 1px solid #7D2252 !important; }

img.user-mug:hover { opacity: 1 !important; }

a.user-mug:hover img { ; }

img.pending-user-mug { 
border: 1px solid #CCC !important;  
opacity: 1 !important;
}

a.pending-user-mug { color:#FCDFFF !important; }

.nav_tab { 
width: 125px !important;  
text-align:center !important;
}

.nav_bar {  
font-size:12px !important; 
padding: 4px !important;
}

.nav_highlight, .nav_highlight * { 
/* top navigation chosen */
color: #ffffff !important; 
background-color: #8D4C5B !important;
}

.nav_dim,.nav_dim * { 
/* top navigation unchosen */
color: #C07878 !important;  
background-color: #471525 !important;
}

.nav_dim *:hover, .nav_highlight *:hover { color: #FBB117 !important; }

b.tabtop, b.tabbottom{
display: block !important;
background-color:#151B54 !important;
}

b.tabtop b, b.tabbottom b {
display: none !important; 
height: 1px !important; 
overflow: hidden !important;
}

b.tabc1 { 
/* don't touch */
margin: 0 5px; 
} 

b.tabc2 { 
/* don't touch */
margin: 0 3px; 
}

b.tabc3 { 
/* don't touch */
margin: 0 2px; 
}

b.tabtop b.tabc4, b.tabbottom b.tabc4 {
/* don't touch */
margin: 0 1px; 
height: 2px;
}

input, textarea { border: 1px solid #cccccc !important; }

textarea { overflow: auto; }

input.submitbtn { 
cursor: pointer !important; 
color: #A6A6A6 !important;  
text-decoration: none !important;  
font-weight: bold !important;  
background-color: #80596F!important;  
border: 1px solid #CCCCCC !important;    
}

input.submitbtn_hover { 
cursor:pointer !important;  
border: #CCCCCC !important;
color: #80596F !important;
}

a.bbtool img { background-color: #80596F !important; }

a.bbtool:hover { opacity: 1 !important; } 

.mc-mug { 
display:inline !important; 
float:left !important; 
padding: 4px !important;
}

.mc-mug img { 
border:1px solid #CCCCCC!important;
}

.mc-list {  
list-style: none !important; 
margin: 0 !important; 
padding: 0 !important;
}

.mc-name { 
margin:0 !important; 
display:inline !important;  
font-size: 12px !important; 
padding: 2px !important;
}

.mc-time { 
display:inline !important; 
position:relative !important; 
top: 0 !important;  
font-size: 10px !important; 
float:right !important; 
color:#786060!important;
}

.mc-body { 
padding:4px !important;  
font-size: 13px !important;
}

.fp-mug {  
text-align:left !important; 
padding: 4px !important;   
text-align:center !important;
}

.fp-mug img { border:1px solid #CCCCCC !important; }

.fp-profile {  
background-color: #151B54 !important; 
width:120px !important; 
padding:4px !important; 
margin: 8px 0px 8px 0px !important; 
height:100% !important;
}

.fp-list {  
background-color: #7F7F7F !important; 
height:100% !important; 
margin: 8px !important; 
padding: 4px !important;
}

.fp-name {  
text-align:center !important; 
margin:0 !important;   
font-size: 18px !important; 
padding: 4px !important;
color: #A6A6A6 !important;
}

.fp-time { 
display: inline !important;  
font-size: 12px !important; 
padding:4px !important; 
float: right !important; 
color: #A6A6A6 !important;
}

.fp-body { 
padding:4px !important;  
font-size: 13px !important;
}

.ft table tr td { 
/* FORUM POST BODY */
background-color: #471525 !important; 
}

.pm-list a {  
text-decoration: none !important;  
padding: 8px !important; 
height: 100% !important; 
width: 100% !important;   
font-weight: normal !important;
}

.bb-quote {  
font-size: 12px !important; 
color: #C48793 !important; 
background-color: #660044 !important; 
border: 1px dotted #CCCCCC !important; 
padding: 8px !important; 
margin: 8px !important; 
width: 90% !important;
}

.bb-quoters { 
font-family: "Times New Roman" !important;  
font-size: 40px !important; 
float: left !important; 
position: relative !important; 
top: -15px !important; 
color: #827839 !important;
}

.bb-quoters2 {  
font-family: Times !important;  
font-size: 40px !important; 
float: right !important; 
position: relative !important; 
top: -15px !important;   
color: #827839 !important;
}

table.buddy-list tr:hover td { background-color: #8b3a62 !important; }

table.buddy-list * a {  
text-decoration: none!important;  
font-weight: normal !important;
}

table.budy-list { width: 40% !important; }

.paypalsubmitimage { border: 0px; }

.paypalsubmitimage:hover { opacity: 1 !important; }

.crstar:hover img { opacity: 1 !important; }

#usersignup.submitbtn {
color: #8b3a62;
}
}
