    0 1px 0 rgba(0,0,0,.3) inset,
    0 -1px 5px rgba(255,255,255,.8) inset,
    5px 0 5px rgba(200,200,200,.6) inset,
    -5px 0 5px rgba(200,200,200,.6) inset,
    0 1px 2px rgba(0,0,0,.8);
