/* Version */
#abs_footer:after { 
	content:"Read it Later Restyled v2.0"; 
	color: #666; 
	font-family: "Lucida Grande"; 
	padding:3px 25px 0 0; 
	float:right; 
	}
/* Background image */
body { 
	font-family: Arial, Helvetica, sans-serif!important;   
	font-size: 80%; 
	line-height: 1.4; 
	background:#353535 url(data:image/gif;base64,) repeat-x left top;
	}
small { font-size: .9em; }
a { 
	color: #763600; 
	text-decoration: underline; 
	}
a:hover { text-decoration:none; }
form { margin-bottom:25px; }
#container { background-color:#eee; }
#content { 
	padding:18px; 
	background-color: #fff; 
	}
#abs_footer { 
	padding:10px 0; 
	margin:0 auto;  
	color:#FECE0A; 
	text-align:left; 
	background-color:#000; 
	background-image: -webkit-gradient(linear, left top, left bottom, from(#333), to(#000), color-stop(0.4, #000));
	background-image: -moz-linear-gradient(top, #333, #000 40%, #000 );
	-webkit-text-stroke:1px transparent; 
	}
#abs_footer a { 
	color:#FECE0A; 
	text-decoration:none; 
	padding:0 5px; 
	font-family: "Lucida Grande"; 
	line-height:2; 
	}
/* RIL icon image */
#abs_footer a:first-child { 
	margin-left:22px; 
	padding-left:17px; 
	background:url(data:image/png;base64,) no-repeat left center; 
	}
#abs_footer a:hover { color:#eee; }
#container, #abs_footer { 
	min-width:425px; 
	max-width:600px; 
	}
#header { 
	display:block; 
	height:auto; 
	padding-top:10px; 
	background-image: -webkit-gradient(linear, left top, left bottom, from(#333), to(#000), color-stop(0.4, #000));
	background-image: -moz-linear-gradient(top, #333, #000 40%, #000 ); 
	}
/* Header clearfix */
#header:after { 
	content:"\0020";
	display:block;
	height:0;
	clear:both;
	visibility:hidden;
	overflow:hidden; 
	}
#nav li { 
	margin-right:2px; 
	padding:0; 
	line-height:1; 
	display: inline;
	}
#nav li a { 
	padding:9px 12px 6px 12px;
	margin-top: 3px;
	color:#666; 
	background-color:#ddd; 
	display:block; 
	text-decoration:none; 
	text-align:center; 
	text-transform:uppercase; 
	border-bottom:1px solid #ccc; 
	min-width:70px; 
	font-size:11px; 
	letter-spacing:1px;
	background-image: -webkit-gradient(linear, left top, left bottom, from(#eee), to(#ccc), color-stop(0.6, #eee));
	background-image: -moz-linear-gradient(top, #eee, #eee 60%, #ccc );
	-webkit-border-top-left-radius: 7px;
	-webkit-border-top-right-radius: 7px;
	-moz-border-radius-topleft: 7px;
	-moz-border-radius-topright: 7px;
	text-shadow:0 1px 1px #bbb;
	}
#nav li a.selected { 
	padding-bottom: 7px;
	padding-top: 12px; 
	margin-top: 0;
	color:#333; 
	font-weight:bold; 
	background:#eee; 
	border-bottom:none; 
	}
#nav li a:hover { 
	text-decoration:none; 
	background:#eee; 
	color:#333; 
	}
#icon_nav { margin-top:5px; }
/* Nav icons */
#icon_nav_add { 
	background-image: url(data:image/png;base64,); 
	}
#icon_nav_add:hover { background-image: url(http://readitlaterlist.com/i/i_add.png); }
#icon_nav_offline { 
	background-image: url(data:image/png;base64,); 
	}
#icon_nav_offline:hover { background-image: url(http://readitlaterlist.com/i/i_down.png); }
#icon_nav_options { 
	background-image: url(data:image/png;base64,); 
	}
#icon_nav_options:hover { background-image: url(http://readitlaterlist.com/i/i_cog.png); }
#options { 
	padding:16px 15px 16px 20px; 
	margin:-18px -18px 0; 
	border-bottom:solid 1px #ccc; 
	background-color:#eee; 
	background-image: -webkit-gradient(linear, left top, left bottom, from(#eee), to(#ccc), color-stop(0.6, #eee)); 
	background-image: -moz-linear-gradient(top, #eee, #eee 60%, #ccc );
	}
#filter { padding:2px 0 3px 20px; }
#list { 
	background-color:#fff; 
	margin: 0 -18px; 
	}
#list li { 
	padding:14px 90px 11px 46px; 
	margin: 0;
	border-bottom:1px solid #ccc; 
	background-image: -webkit-gradient(linear, left top, left bottom, from(#fff), to(#eee), color-stop(0.6, #fff));
	background-image: -moz-linear-gradient(top, #fff, #fff 60%, #eee ); 
	}
#list li .favicon { 
	background-position:center center; 
	margin:10px 0 0 20px; 
	-webkit-border-radius:4px; 
	-moz-border-radius: 4px; /* Doesn't round corners of images in Firefox 3.6b5 :-( Maybe someday ... */
	}
#list li .title { 
	padding-bottom:2px; 
	line-height:1.1; 
	}
#list li .title a { 
	font-size:1.1em; 
	display:block; 
	}
#list li .text, #list li .check { 
	width:20px; 
	height:20px; 
	margin:6px 20px 0 0; 
	border:1px solid #ccc; 
	background-color:#eee;
	-webkit-border-radius: 4px;
	-moz-border-radius: 4px;
	}
#list li a.readd {
	background: #eee url(data:image/png;base64,) no-repeat center center;
	}
#list li a.loading, #list li a.loading:hover { 
	background-color: #fff; 
	}
#list li .text:hover, #list li .check:hover { 
	border-color:#ddd; background-color: #ddd; 
	}
#list.loading { background-position: center 25px!important; }
#list .empty { margin-top: 25px; }
#list a, #footer a { text-decoration: none; }
#list a:hover, #footer a:hover { text-decoration: underline; }
#footer { 
	margin: 0 -18px -18px;
	height: auto;
	background-color:#eee;
	background-image: -webkit-gradient(linear, left top, left bottom, from(#eee), to(#bbb), color-stop(0.4, #eee)); 
	background-image: -moz-linear-gradient(top, #eee, #eee 40%, #bbb );
	}
#pages { 
	padding:12px 20px 5px; 
	position:relative; 
	right:auto; 
	left:auto; 
	top: auto; 
	display: block;
	line-height:3.5; 
	font-size:.8em; 
	color:#333; 
	text-transform:uppercase; 
	}
#pages a:hover { text-decoration:none; }
#pages select { width:50px; }
#next, #previous { 
	text-align:center; 
	padding:7px 14px 6px; 
	margin-right:-3px; 
	letter-spacing:1px; 
	text-shadow:0 1px 1px #bbb; 
	background-color: #ddd; 
	border: solid 1px #aaa;
	background-image: -webkit-gradient(linear, left top, left bottom, from(#eee), to(#ccc), color-stop(0.8, #ccc));
	background-image: -moz-linear-gradient(top, #eee, #ccc 80%, #ccc ); 
	-webkit-border-radius: 5px;
	-moz-border-radius: 5px;
	}
#next.active:hover, #previous.active:hover { 
	background-image: none; 
	color:#666; 
	}
#next { 
	padding:7px 28px 6px; 
	margin-left:4px; 
	}
#previous { margin-right:4px; }
/* Next/prev arrows */
#next::after { content:"\00A0\2192"; }
#previous::before { content:"\2190\00A0"; }
#next.active, #previous.active  { 
	color:#222; 
	font-weight:bold; 
	}
.ninja { color:black; visibility:hidden; }
/* The end */
