css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("boards.4chan.org") {
	body {
		background:url("http://imgur.com/hu5mV.png") !important;
		background-opacity = 0.8;
		background-attachment:fixed !important;
		background-position:bottom right !important;
		background-color:#2C2C2C !important;
		background-repeat:no-repeat !important;
		color: #eee !important;
	}
	* {
		font: 10pt "Lucida Sans Unicode";
	}
	a {
		color: #efefef !important;
	}
	hr {
		display: none !important;
	}
	#navtop, #navbot {
		background: #000;
		color: #000 !important;
		width: 100%;
		text-align: center;
		height: 12pt;
		font-size: 7pt !important;
	}
	#navtop *, #navbot * {
		color: #eee;
		font-size: 7pt;
		display: inline;
	}
	#navtop a:hover, #navbot a:hover {
		background: #555;
	}
	form {
		margin: 1em;
	}
	.inputtext, textarea {
		width: 295px;
	}
	.rules {
		display: none;
	}
	td.postblock {
		background: #444 !important;
		border: 1px solid #666 !important;
		color: #aaa !important;
	}
	td.postblock * {
		font-size: 6pt;
	}
 	td.reply, td.replyhl {
		background-color:#333 !important;
		border: 1px solid #555 !important;
		color: #eee !important;
		-moz-border-radius: 8px;
	}
	td.replyhl {
		background-color:#4B4B4B !important;
		border: 1px solid #4B4B4B !important;
		color: #eee !important;
	}
	.postername, .filesize, span.filetitle, .filesize a, a.linkmail, a.quotejs, .replytitle, .commentpostername, .omittedposts, .abbr {
		color: #fff !important;
		font-size: 8pt;
	}
	span.filetitle, .replytitle {
		color: #e15014 !important;
	}
	#hd, small, font[size="1"], font[size="2"], #ft, #filter-button, #option-button, #navtopr, #navbotr, .logo,.logo font b span, .doubledash, img.rotating, input[type="button"], td[align="right"] {
		display: none !important;
	}
	.pages {
		background: #444 !important;
		border: 1px solid #555 !important;
	}
	.pages td {
		color: #444 !important;
		font-size: 4pt;
	}
	.pages td a, .pages td b {
		color: #eee !important;
		font-size: 8pt;
	}
	.pages td b {
		background-color: #000;
	}
	.pages input {
		display: none !important;
	}
	.pages td:first-child {
		display: none;
	}
	pre, textarea, input:not([type="button"]):not([type="checkbox"]):not([type="submit"]):not([type="reset"]), select {
		font-size: 9pt ! important; color: #fff !important; background: #333 !important; border-color: gray !important;
	}
	input[type="submit"] {
		background: #444 !important;
		color: #aaa !important;
		border: 1px solid gray;
		-moz-appearance: none;
		font-size: 9pt;
	}
	th {
		background: #222 !important;
	}
	th font {
		font-size: 7pt;
	}
	input[type="file"] {
		opacity: 0.12 !important;
	}
	td.deletebuttons {
		font-size: 8pt !important;
		color:#eee !important;
	}
	.commentpostername a.linkmail[href="mailto:sage"]:after {
		content: " (sage)";
	}
}
