  display: none !important;
}

INPUT.chooser_input_url_2 {
  float: left !important;
  width: 260px !important;
  border: 1px solid #93AC1D !important;
  padding-left: 3px !important;
  padding-top: 2px !important;
  color: #8AA700 !important;
}

