  width: 95px !important;
}

.welcome_container {
  display: none !important;
}

.info_container {
  display: none !important;
}

DIV.download_container {
  width: 800px !important;
}

DIV.faq_container {
  width: 800px !important;
}

DIV.top20_container {
  width: 800px !important;
}

TABLE.top_20_url {
  width: 780px !important;
}

SPAN.footer-menu {
  float: left !important;
}

DIV > TABLE {
  height: 1px !important;
