css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://example.com/cgi-bin/gnatsweb.pl") {
  body {
    background-color: #fff;
    font-family: Helvetica, Arial, sans-serif;
    margin: 0 auto;
    width: 960px;
  }

  table:first-child td {
    background-color: rgb(37%,37%,32%);
  }

  table, td, th {
    border: none !important;
  }

  tr:hover td {
    background-color: #ffc;
  }

  table:first-child tr:hover td {
    background-color: rgb(37%,37%,32%);
  }

  table {
    max-width: 960px;
  }

  form td {
    border-style: solid !important;
    border-width: 0 0 1px 0 !important;
    border-color: #eee !important;
    padding: 4px !important;
  }

  th {
    background-color: rgb(97%,97%,92%);
    padding: 2px 0;
  }

  pre {
    background-color: rgb(97%,97%,92%);
    font-family: "Lucida Console", monospace;
    font-size: small;
    margin: 0;
    padding: 0.5em 8px;
  }

  h1, h2, h3 {
    letter-spacing: -0.05em;
  }

  h1 {
    border-bottom: 0.1em solid #c60;
    color: #c60;
    font-size: xx-large;
  }

  h3 {
    border-bottom: 0.1em solid #09c;
    color: #09c;
    font-size: x-large;
    line-height: 0.9em;
    margin: 0;
    padding-left: 8px;
  }

  hr {
    visibility: hidden;
  }

}
