css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("stern.de") {

body
{
  font-size: 1em !important;
  background: #fff !important;
}

#TopTeaser,
#TopContent,
#navTop,
#navSub,
#navSubRahmen,
#contentContainer,
#mainTeaser,
#L3NavExtra,
.alleExtras,
#contRight h2,
.sidebarTeaserKasten,
.erlesen,
.artikelBlock,
#navFooter,
.leftTeaser,
.mainTeaser
{
  width: auto !important;
}

#TopContent
{
  float: none !important;
}

#TopContent .next,
.leftTeaser .next
{
  clear: none !important;
}

#navTop
{
  background: #c00 !important;
}

#contMain
{
  width: 65% !important;
  padding: 0 6px !important;
  margin: 0 !important;
}

#contRight
{
  margin: 0 !important;
  width: 33% !important;
}

.imageRelative
{
  display: none !important;
}

#navFooter,
#navFooter .grau
{
  font-size: 0.8em !important;
}

#adRahmen,
#banner,
#contentAd,
#contenttextlink,
#sky
{
  display: none !important;
}

.mainTeaser
{
  clear: left !important;
}

}
