css:
/*
* Author: Will (war59312@gmail.com)
* Description: Hides all the ads on webhostingtalk.com
*/

/* +++ changelog +++
April 18th, 2010
- original release
*/

@-moz-document domain(webhostingtalk.com) {

/* SUPER HEADER TEXT AD */
#promotion { display: none !important; }

/* HEADER - POWERED BY RACKSPACE AD */
.powered { display: none !important; }

/* HEADER AND FOOTER RACKSPACE AD */
.advertisement { display: none !important; }

/* "Upgrade Your Membership" TEXT AD */
A[href="/memberships/"] { display: none !important; }

/* RIGHT SIDE-BAR AD */
#ad { display: none !important; }
TD[width="200"][valign="top"][align="right"] { display: none !important; }

/* SPONSORED FOOTER ADS */
#footerad, #sponsored, #sponsored-clear { display: none !important; }

/* FOOTER */
#resources { display: none !important; }

}
