css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("alfaclub.pl")
{
 body { background-color: #304030 !important; }
 .postbody { color: #e7f7e7 !important; }
 .quote, .code { background-color: #1c2c1c !important; color: #c7d7c7 !important; }
 .bodyline { background-color: #667666 !important; }
 .forumline { background-color: #667666 !important; }
 hr { border: solid #b0d0b0 0px !important; border-top-width: 1px !important; }
 a:link { color : #d7f0d7 !important; }
 a:active { color : #d7f0d7 !important; }
 a:visited { color : #a7c0a7 !important; }
 td.row1 { background-color: #3f4f3f !important; }
 td.row2 { background-color: #2f3f2f !important; }
 td.row3 { background-color: #3f4f3f !important; }
 .rowpic { background-color: #3f4f3f !important; }
 .cattitle { color: #c5e0c5 !important; }
 td.cat,td.catHead,td.catSides,td.catLeft,td.catRight,td.catBottom { background-image: none !important; background-color:#2c3c2c !important; }
 td.row3Right,td.spaceRow { background-color: #3f4f3f !important; }
 th { background-color: #4f5f4f  !important; background-image: none !important; }
 input.post, textarea.post, select, .liteoption, .button { background-color : #445544 !important; }
 .mainoption { background-color : #81a181 !important; color : #FFFFFF !important; }
}
