css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dribbble.com") {

#dashboard,
ol.dribbbles li ul.tools,
ol.dribbbles li h2,
#secondary .ad-top-wrap + h3,
#secondary ol.players-list,
div.single-title a,
span.screenshot-dash strong.views,
div.single-title h2,
div.favm,
#secondary h3.more-from.more-from-player,
#secondary h3.more-from.more-from-player + ol,
#comments,
div.the-rebound h4,
#main div.col-50,
#like-section .fav-number { display: none !important }

#comments-section:hover #comments { display: block !important }

#main div.col-50-last { float: left !important; width: 700px !important; }

#comments-header:hover { cursor: help !important; }

ol.players-list li { width: 330px !important; float: left !important; margin: 0 10px !important; }

div.page, .pagination { clear: both !important; }

ol.dribbbles li { margin-bottom: 10px !important }

div.single-title h1, div.single-title h2 { margin-left: 0 !important; }

}
