css:
/**

Theme: Zesty ashes
By: HexD

**/


@namespace url(http://www.w3.org/1999/xhtml);

/** cs-manager.com only: **/
@-moz-document domain(cs-manager.com) {

body {	
	background: #bbb !important;
	background-image: url('http://hexd.wordpress.com/files/2007/07/supersexy.jpg');
	background-repeat: no-repeat;
	background-attachment: fixed
}

a {
	color: #9C7127 !important;
/*	font-style: italic !important; */
}

/** Add remote logo as bg and change colors of header **/
#header {
	background-image: url("http://hexd.wordpress.com/files/2007/07/csmlogo.png") !important;
	background-repeat: no-repeat !important;
	background: #bbb !important;
	border: 1px solid #ccc !important;
	margin: 0 auto !important;
}

/** Hide original logo: **/
#header img {	visibility: hidden !important;
} /** ingame **/

#header a img {	visibility: hidden !important;
} /** front page **/

/** box containing it all and a few elements, that shouldnt have any specific colour **/
#container{ /* Center content*/
	color: #9C7127 !important;
	background: #aaa !important;
	margin: 0 auto !important;
	border-top: 1px solid #000 !important;
	border-left: 1px solid #ccc !important;
	border-bottom: 1px solid #000 !important;
	border-right: 1px solid #ccc !important;
}

.post {
	color: #9C7127 !important;
	background: #aaa !important;
	border: 1px solid #ccc !important;
	margin: 0 auto !important;
}

#footer_container {
	color: #9C7127 !important;
	background: #aaa !important;
	/**border: 1px solid #ccc !important;**/
	margin: 0 auto !important;
}

#content {
	color: #000 !important;
	background: #aaa !important;
	margin: 0 auto !important;
	/**border: 1px solid #ccc !important;**/
}

/** adding borders to some tables **/ 
table.rank table.login table.rank table.info table.mail table.login {
	background: #aaa !important;
	border: 1px solid #ccc !important;
}

td.space { /* GAPS BETWEEN ALL CENTER CONTENT */
	background: #aaa !important;
	border:none !important;
}

div.space { /* gaps between left and right menus */
	background: #aaa !important;
	border:none !important;
}
/* NOT FOUND*/
a.player { 
	background: #aaa !important;
	color: #ffff00 !important;
}
/* ADVANCED GUN MENU TEXT*/
a.tactic { 
	background: #aaa !important;
	color: #ff00ff !important;
}
/* NOT FOUND*/
td.tactic { 
	background: #aaa !important;
	color: #00ff00 !important;
}
/* DETAILED PLAYER INFO (EXTRAINFO) */
td.top { 
	background: #bbb !important;
	color: #ff0000 !important;
}
/*NOT FOUND*/
td.player { 
	background: #aaa !important;
	color: #0000ff !important;
}
/*NOT FOUND*/
a.tactic:hover {
	background: #aaa !important;
}
/* TACTICS POSITION TABLE*/
td.position {
	background: #282828 !important;
	color: #9C7127 !important;
	border: 1px solid #ccc !important;
}

/** MAIN CONTENT HEADERS (MOST OF THEM)**/
td.head {
	color: #9C7127 !important;
	background-color: #ccc !important;
	background-image: none !important;
	border: 1px solid #ccc !important;
}
/* LEFT AND RIGHT MENU AND SUB HEADER FOR SOME PAGES*/
h3 {
	color: #9C7127 !important;
	background-color: #ccc !important;
	background-image: none !important;
	border: 1px solid #ccc !important;
}
/* MAIN HEADERS FOR SOME PAGES*/
h2 {
	color: #9C7127 !important;
	background-color: #ccc !important;
	background-image: none !important;
	border: 1px solid #ccc !important;
}

/* LEFT AND RIGHT MENU BACKGROUNDS*/
li a {
	background: #bbb !important;
	color: #000000 !important;
	border-bottom: 1px solid #ccc !important;
}

/** menu specific borders **/
ul#menu li a {	
	border: 1px solid #ccc !important;
}

/** TAB BORDERS (PRESENTATION) **/
ul#tab li a {
	background: #aaa !important;
	color: #846736 !important;	
	border: 1px solid #ccc !important;
}
/* HOVER COLOUR MENUS*/
li a:hover {
	background: #9C7127 !important;
	color: #000000 !important;
}

/** SSELECTED TABS **/
ul#tab li span {
	background: #aaa !important;
	color: #9C7127 !important;
	border: 1px solid #ccc !important;
}

/** other spans (eg online in freinds list) **/
li span {
	color: #846736 !important;
}

/** color of areas surrounding maillist **/
table.mail tr.head td {
	background-color: #aaa !important;
}

table.mail td.delete {
	background-color: #aaa !important;
}

table.mail td.select {
	background-color: #aaa !important;
}

/** dark area below the left and right menus **/
#menu, #footer_left  {
	background: #aaa !important;
}

div#container>div#info {
	background: #aaa !important;
}

#footer_right {
	background: #aaa !important;
}

/** search area **/
form#search {
	border-bottom: 1px solid #ccc !important;
	background: #bbb !important;
}

/**input fields and buttons **/
input,select,, {
	background: #bbb !important;
	border-top: 1px solid #000000!important;
	border-left: 1px solid #666666 !important;
	border-right: 1px solid #000000 !important;
	border-bottom: 1px solid #888888 !important;
}

radio {
	background: #bbb !important;
	border-top: 1px solid #000000!important;
	border-left: 1px solid #666666 !important;
	border-right: 1px solid #000000 !important;
	border-bottom: 1px solid #888888 !important;
}

textarea {
	background: #bbb !important;
	border-top: 1px solid #000000!important;
	border-left: 1px solid #666666 !important;
	border-right: 1px solid #000000 !important;
	border-bottom: 1px solid #888888 !important;
}

.buttonsmall {
	background: #555555 !important;
	color: #000000 !important;
	border-top: 2px solid #888888!important;
	border-left: 2px solid #000000 !important;
	border-right: 2px solid #666666 !important;
	border-bottom: 2px solid #000000 !important;
	font-size: 10px !important;
	padding: 0px !important;
}

.button {
	background: #555555 !important;
	color: #000000 !important;
	border-top: 2px solid #888888!important;
	border-left: 2px solid #000000 !important;
	border-right: 2px solid #666666 !important;
	border-bottom: 2px solid #ccc !important;
	font-size: 10px !important;
	padding: 0px !important;
}

/** news items (community news) **/
td.value {
	background: #bbb !important;
}
td.foot {
	background: #bbb !important;
}

td.info {
	background: #bbb !important;
}

/** Training room colours **/
#training_map {
	background: #bbb !important;
}

#training_player {
	background: #bbb !important;
}

#skill {
	background: #bbb !important;
}

#skill,#trainer {
	background: #bbb !important;
}

#training_players {
	background: #bbb !important;
}

div#training_type {
	background: #bbb !important;
}

/** FORUM **/
tr.even {
	background: #ababab !important;
}
tr.odd {
	background: #aaa !important;
}
div.modify {
	background: #bbb !important;
}
}
