css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reddit.com") {
* { font-family: "Helvetica Neue", Helvetica, "MgOpen Moderna", sans-serif !important;}
body {
  background: #333 !important;
}
/*-------------------
       HEADER
-------------------*/
#header {
  border-bottom: none !important;
  background: #222 !important;
}
#header-bottom-right {
  background: #333 !important;
}
#sr-header-area, #sr-more-link {
  background: #333 !important;
  border-bottom: 1px double #111 !important;
  font-size: 10px !important;
 }
    #sr-bar a, #header-bottom-right a {
      color: #cee3f8 !important;
    }
    #sr-bar a:hover, #header-bottom-right a:hover {
      color: orangered !important;
      text-decoration:none !important;
    }

    #sr-bar .selected a,#sr-more-link, .tabmenu li.selected a{
      color: orangered!important;
    }
    #sr-more-link {
      font-weight: normal !important;
      margin: -3px 0 0 !important;
      padding-top: 3px !important;
      height: 16px;
    }
/*-------------------
     MY REDDITS
-------------------*/
.dropdown.srdrop .selected {
  color: #fff !important;
background: right no-repeat url("data:image/gif;base64,R0lGODlhEAAQALMKAP///9FHE/9xPP9yPv9qM65TMcZLHf9aHf9pMf91Qv///wAAAAAAAAAAAAAAAAAAACH5BAEAAAoALAAAAAAQABAAAAQwUMlJq704Y8C7BkEYAF9ACOPGmahXAUU4DGFBWqCQDEi6GYKDLwMwDD83jXLJbGYiADs=") !important;

}
.drop-choices {
top: 13px !important;
left: 0px !important;
background: #333 !important;
-moz-border-radius: 7px;
border: 1px double #333 !important; 
}

.drop-choices a:hover {
background: #222 !important;
color: #cee3f8 !important;
}
/*-------------------
      TABMENU
-------------------*/
.tabmenu li {
  font-weight: normal !important;
}
.tabmenu li.selected a, .tabmenu li a {
  -moz-border-radius-topleft: 7px;
  -moz-border-radius-topright: 7px;
}
.tabmenu li a{
  background: #2d2d2d!important;
  color: #1e90ff !important;
}
.tabmenu li a:hover{
  background: #333 !important;
  color: #45a2fd !important;
}
.tabmenu li.selected a {
  background: #333 !important;
    font-weight: bold !important;
  border: none !important;
}

/*-------------------
       SIDEBAR
-------------------*/
.side {
  width: 225px !important;
  background: none !important;
}
#search input[type="text"] {
  background: #22598f 4px 2px no-repeat url("data:image/png;base64,") !important;
  color: #eff7ff !important;
  margin: -8px 0 0 4px !important;
  padding: 0 2px 0 1px !important;
  height: 17px !important;
  width: 221px !important;
  text-align: right;
  font-size: 12px !important;
  -moz-border-radius-bottomleft: 7px;
  border-color: #22598f !important;
}
#search input[type="text"]:hover {
  background-color: #1e90ff !important;
  border-color: #1e90ff !important;
}
.side .submit {
  float: right !important;
  margin-top: -16px !important;
  margin-right: -1px !important;
  width: 100px;
}
.side .create {
  margin-left: 3px !important;
  margin-top: -16px !important;
  width: 150px;
}
  .submit .morelink{
    text-align: right !important;
  }
  .morelink {
    font-size: 12px !important;
    font-weight: normal !important;
    text-align: left !important;
    background: none !important;
    border: 0 !important;
  }
.morelink .nub ,.sidebox .spacer,.sidecontentbox,.side .usertext{
  display: none !important;
}

.fancy-toggle-button .remove, .fancy-toggle-button .active {
  color: #333 !important;
}

.linkinfo {
-moz-border-radius:7px 0 0 7px !important;
border-right: 0 !important;
margin-right: -5px !important;
}

/*-------------------
        LINKS
-------------------*/
a, a:visited {
  color: #1e90ff !important;
}
a:hover, a:active,.entry .buttons li a:hover {
  color: orangered !important;
}
.entry .buttons li a {
  color: #888 !important;
}
/*-------------------
     REDDITS
-------------------*/
a.thumbnail {
  text-align: center !important;
  border: 1px double #222;
  background: #777 !important;
}
.thumbnail img {
  padding: 4px !important;
}
.score {
  padding-top: 2px !important;
  font-size: 12px !important;
  font-weight: normal !important;
  color: #666 !important;
}
.rank {
  display: none !important;
}
.thumbnail,.entry {
    margin: 0 10px 0 0 !important;
}
.rank,.midcol {
    padding-top: 5px !important;
}
.odd {
  background: #2d2d2d !important;
}
.midcol .down, .likes .down {
    background: center no-repeat url("data:image/png;base64,") !important;
}
.midcol .up, .dislikes .up {
    background: center no-repeat url("data:image/png;base64,") !important;
}
.dislikes .downmod {
    background: center no-repeat url("data:image/gif;base64,") !important; 
}
.likes .upmod {
    background: center no-repeat url("data:image/gif;base64,") !important; 
}
.expando-button {
  background-color: transparent !important;
}
/*-------------------
     COMMENT
-------------------*/
.infobar {
  color: #cee3f8 !important;
  background: #333 !important;
  border: 1px double #333 !important;
  -moz-border-radius: 7px;
}
textarea,.usertext button {
  color: #1e90ff !important;
}
.usertext .save {
  color: orangered !important;
  background: #000 left no-repeat url("data:image/png;base64,") !important;
}
.markhelp {
  -moz-border-radius: 7px;
  border-collapse: separate !important;
  border-color: #333 !important;
  color: #eff7ff;
  background: #333 !important
}
.markhelp td {
  background: #222 !important;
  border-color: #222 !important;
}
.help-toggle .option{
  padding-right: 20px !important;
  font-size: 12px !important;
  background: right no-repeat url("data:image/png;base64,") !important;
}
/*-------------------
    SUBMIT NEW
-------------------*/
.tabmenu.formtab a {
  background: #333 !important;
  color: #cee3f8 !important;
  text-transform: uppercase;
}
.tabmenu.formtab a:hover {
  background: #555 !important;
  color: #fff !important;
}


.formtabs-content {
  border-top: 2px double #333 !important;
}

.roundfield {
  background: none !important;
}

.roundfield textarea,.roundfield input[type="text"], .roundfield input[type="password"]  {
  background: #333 !important;
  -moz-border-radius: 7px;
  color: #cee3f8 !important;
}
.roundfield .title {
  text-transform: uppercase;
}
.searchpane #search input[type="text"] {
  text-align: left !important;
  color: orangered !important;
  margin-top: 0 !important;
  background: #fff !important;
  -moz-border-radius: 3px;
  border-color: #333 !important;
}
/*---------------
   READ MORE
---------------*/
.link .usertext .md {
  background: #333 !important;
  border: 1px double #000 !important;
  color: #cee3f8 !important;
}
.nsfw-stamp {
  background: orangered !important;
}
.rounded {
  border: 1px double #222 !important;
  -moz-border-radius: 5px !important;
}

}
