css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("t.qq.com") {
body{
background: url("data:image/png;base64,") no-repeat scroll #C0DEED !important; 
}

#mainWrapper .main{
background: white !important;
border-right: 1px solid #C0DEED;
-moz-border-radius: 6px 0px 0px 6px;
-webkit-border-radius: 6px 0px 0px 6px;
border-radius: 6px 0px 0px 6px;
}

#mainWrapper.whiteBg, .whiteBg {
background: white !important;
}

#mainWrapper {
min-height:200px !important;
background: #DDEEF6 !important;
-moz-border-radius: 6px;
-webkit-border-radius: 6px;
border-radius: 6px;
}

#mainWrapper #talkBox, .topMenu, .moreFoot, .moreFoot a, .SM li:hover, .SM li.hover, .SM li, #pageNav, .blueFoot {
background: none !important;
}


.SM li.current {
background: white !important;
-moz-border-radius: 0px 6px 6px 0px;
-webkit-border-radius: 0px 6px 6px 0px;
border-radius: 0px 6px 6px 0px;
border-top: 1px solid #C0DEED;
border-bottom: 1px solid #C0DEED;
border-right: 1px solid #C0DEED;
margin-right: 14px !important;
}

.SM li:hover {
background: #C0DEED !important;
-moz-border-radius: 0px 6px 6px 0px;
-webkit-border-radius: 0px 6px 6px 0px;
border-radius: 0px 6px 6px 0px;
margin-right: 14px !important;
}

#headRound, #footRound, .standard {
visibility: hidden;
}

.goTop {
border-left: none !important;
}
}
