css:
/*
 * HTNRG - Skin : AeroTrick2  [Hattrick NewD]
 * 
 * Author      : _eNeRGy_ (7397517)
 * LogoCreator : fabricep (3898485)
 * Created     : Apr 22th, 2010
 * Version     : 14
 * Website     : url(http://nrgjack.altervista.org/)
 *
 */ 
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document 
domain("hattrick.ws"),
domain("hattrick.interia.pl"),
domain("hattrick.uol.com.br"),
domain("hattrick.org") {

/* 
 ********
 * HTML *
 ********
 */
a {
	color:maroon!important;
}
a:hover{
	color:orange!important;
	text-decoration:none!important;
}
a.shy {
	color:#999!important;
	font-style: italic;  
}
a.bookmarkedUserWrote{
	color:orange!important;
}
.hattrick,.hattrickNoSupporter,#page{
	width:987px;
}
body{
	background: url(http://nrgjack.altervista.org/_altervista_ht/style/aerotrick2/vbg.png)   !important;
}
img[src*="/micro_star.gif"] {
	padding: 15px 0 0 16px!important;
	width: 0 !important;
	height: 0 !important;
	background-image: url(http://nrgjack.altervista.org/_altervista_ht/style/stelle/b_star.png)  !important;
}
img[src*="/page/master_page.png"] {
	visibility:hidden  !important;
}
#top{
	display:none!important;
}
#page{
	border: 1px solid orange !important;
	margin:2px auto!important;
	-moz-border-radius : 9px !important;
}
#header, #content, #bottom, #page{
	background: url(http://nrgjack.altervista.org/_altervista_ht/style/aerotrick2/obg.png)   !important;

}
input[id*="Stats"][type="text"]{
	width:95px;
}
/* 
 **********
 * HEADER *
 **********
 */

#teamLinks,
#online,
#time{

}
#teamLinks a,
#online,
#time,
#ticker a,
#footer a{
	color:white!important;
}
#teamLinks a{
	text-decoration:none!important;
	font-weight:bold!important;
	font-size:larger!important;

}

#teamLinks a:hover,
#ticker a:hover{
	color:maroon!important;
}
#logo,
#stageLogo {
	background: transparent url(http://nrgjack.altervista.org/_altervista_ht/style/aerotrick2/Aero2.png)  no-repeat scroll left top !important;
	left:-15px!important;
}

#stageLogo {
height:25px!important;
padding-bottom:20px!important;
margin-top:auto!important;
}


#ticker div {
	padding-left: 18px!important;
	background-image: url(http://nrgjack.altervista.org/_altervista_ht/style/stelle/y_star.png)  !important;
}
#ticker {
	left : 265px!important;
}
div.scContainer a:hover{
	opacity:0.6!important;
}
.subMenuBox h3,
.boxBody h2,
h3.feed{
	color:black!important;
	background: #FFDE96!important;
	border: double white !important;
	text-align:center!important;
	-moz-border-radius : 9px !important;
	-webkit-border-radius : 9px !important;
	padding:3px!important;
	opacity:0.7!important; 
}
/* 
 ********
 * MENU *
 ********
 */
#menu {
	background: white none  no-repeat scroll left top !important;
	-moz-border-radius: 9px;
	-webkit-border-radius: 9px;
	border: 1px solid white;
	height:30px!important;
	padding-left:3px!important;
	opacity:0.4!important;
}
#menu a{
	line-height: 30px !important;
	font-size:larger!important;

}
#menu a[href="/MyHattrick/"],
#menu a[href="/Club/"],
#menu a[href="/World/"],
#menu a[href*="/Forum/"],
#menu a[href="/Shop/"],
#menu a[href*="Signup"],
#menu a[href*="/Help/"],
#menu a[href*="action=logout"]{
	color: black !important;
}
#menu a[href*="/Stage/StagePages.aspx"]{
	color:#FF9900!important;
}
#menu a:hover{
	background: #FFDE96 !important;

}
/* 
 ***********
 * BOX TOP *
 ***********
 */

.sidebarBox .boxHead h2, 
.subMenuBox .boxHead h2, 
.main .boxHead h2,
.sidebarBox .boxHead .boxLeft h2,
.subMenuBox .boxHead .boxLeft h2,
.main .boxHead .boxLeft h2{
	background: #fff none  no-repeat scroll left top !important;
	border: 1px solid white !important;
	color:#000!important;
	opacity:0.4!important;
}

#header,
.sidebarBox .boxHead h2, 
.subMenuBox .boxHead h2, 
.main .boxHead h2,
.sidebarBox .boxHead .boxLeft h2,
.subMenuBox .boxHead .boxLeft h2,
.main .boxHead .boxLeft h2{
	-moz-border-radius-topleft : 9px !important;
	-moz-border-radius-topright : 9px !important;
	margin-left:0px !important;
}
.sidebarBox .boxHead , 
.subMenuBox .boxHead , 
.main .boxHead ,
.sidebarBox .boxHead .boxLeft,
.subMenuBox .boxHead .boxLeft,
.main .boxHead .boxLeft{
	background: transparent none  no-repeat scroll left top !important;
}
/* 
 **************
 * BOX CENTER *
 **************
 */
.main .boxHead h2 span, 
.main .boxHead h2 a {
	color:#000!important;
}
.main .boxHead h2 a:hover{
	color:#fff!important;
}
.sidebarBox .boxBody, 
.subMenuBox .boxBody, 
.main .boxBody {
	background:#fff none repeat scroll 0 0!important;
	border-left:1px solid  white !important;
	border-right:1px solid  white !important;
}
/* 
 **************
 * BOX BOTTOM *
 **************
 */
.sidebarBox .boxFooter, 
.subMenuBox .boxFooter, 
.main .boxFooter {
	background: #fff none  no-repeat scroll left top !important;
	border-top: none !important;
	border-left: 1px solid white !important;
	border-right: 1px solid white !important;
	border-bottom: 1px solid white !important;
}
.sidebarBox .boxFooter .boxLeft, 
.subMenuBox .boxFooter .boxLeft, 
.main .boxFooter .boxLeft{
	visibility:hidden !important;
}
.sidebarBox .boxFooter, 
.subMenuBox .boxFooter, 
.main .boxFooter,
#bottom{
	-moz-border-radius-bottomleft : 9px !important;
	-moz-border-radius-bottomright : 9px !important;
}
/* 
 *********
 * FORUM *
 *********
 */
.cfWrapper {
	border: 1px solid #333  !important;
}
.cfHeader {
	background: #333  !important;
	color: #fff  !important;
}
.cfHeader a{
	color: white  !important;
}
.cfHeader a:hover{
	color: #D2691E  !important;  
	text-decoration:none!important;  
}

/* 
 *****************
 * FORMATION FIX *
 *****************
 */


	
.ui-tabs .ui-tabs-nav li a {
	color:#eee!important;
}

#orders .substitution .moreConditions {
	color:#fff;
}

a[id*="advancedLink"]{
	color:yellow!important;
	text-decoration:none!important;
	font-weight:bold!important;
	right:11px!important;
}

div.minifieldWrapper{
	margin: 0 23px 10px 4px!important; 
}

#orders .substitution {
	min-height:95px!important;
}

a#editLineupDone{
	color:red!important;
	font-weight:bold!important;
}

#orders input#newLineup{
	width:190px;
}

div#order_tabs> .ui-tabs-nav li {
	background:none repeat scroll 0 0 #1C3F16!important;
}

div#order_tabs> .ui-tabs-nav li.ui-tabs-selected a {
	color:yellow!important;
	background:none repeat scroll 0 0 #40823D!important;
}
div#tabs_lineups> .ui-tabs-nav li {
	background:none repeat scroll 0 0 #333!important;
}

div#tabs_lineups> .ui-tabs-nav li.ui-tabs-selected a {
	color:yellow!important;
	background:none repeat scroll 0 0 #000!important;
}

#details{
	top:320px!important;
min-height:200px!important;
left:auto!important;
right:134px!important;	
}


/* 
 ************
 * FOXTRICK *
 ************
 */
#ft_bonuswage{
	color:#D2691E!important;
	font-weight:bold!important;
}
#ft_deadline{
	color:#D2691E!important;
}
span.highlight{
	color:#D2691E!important;
}
#ft_rapidid a{
	color:#fff!important;
}
#ft_rapidid a{
	text-decoration:none!important;
	font-weight:bold!important;
	font-size:larger!important;
}
#ft_rapidid {
	margin-top:10px!important;
}
#ft_rapidid_form {
	margin-top:-5px!important;
}
#ft_rapidid a:after{
	content:", "!important;
}
#ft_rapidid_input_senior-team{
	height:11px!important;
}
#ft_rapidid a:hover{
	color:#fff!important;
}
}
