css:
/* ===================================================================
 *　Apprentice [goo mail - Simply]  Ver. 2.0.0
 *　シンプル版統一後の無料版gooメールの広告と余分な部分を非表示にするCSSです。
 *　キャラクター版及び公式版のデザイン変更にも対応。
 * ===================================================================
 *　Ver. 2.0.0　2013/03/25　　ドコモwebメールの停止により名称変更と該当部分のCSS排除
 * ================================================================ */

/* ==================== Cascading Style Sheets ==================== */
@charset       "UTF-8";
@namespace url(http://www.w3.org/1999/xhtml);

/* gooID ログイン
 *========================= */
@-moz-document domain("login.mail.goo.ne.jp") {

#main img[src*="adcdn.goo.ne.jp"][src$=".jpg"] {
	display: none !important;
	}
#ad {
    height: 0px !important;
    }
#toppr {
    height: auto !important;
    }
#toppr > dl {
    margin-top: 0px !important;
    }

}

/* gooメール
 *========================= */
@-moz-document url-prefix("http://pc.mail.goo.ne.jp/s/") {

/* ad
 *--------------- */
#main img[src*="adcdn.goo.ne.jp"][src$=".jpg"],
.box > p,
#lside div.banner,
#lside > center,
#goonotice {
 	display: none !important;
	}

/* body
 *--------------- */
body {
	padding-top: 3px !important;
	}

/* #header
 *--------------- */
#logo,
#headnavi,
#hpr,
#header .bline {
	display: none !important;
	}

/* #contents
 *--------------- */
#incontents .clearboth,
#incontents .goonews,
#incontents .gooranking,
#incontents .gooblogranking,
#incontents .banner,
#navattype {
	display: none !important;
	}

#prbox {
	margin: 0px !important;
	}

#incontents .toolbtm {
	margin-bottom: 5px !important;
	}

#incontents .paging {
	margin: 0px 40px !important;
	}

#incontents .switchnum {
	margin-bottom: 0px !important;
	}
 
 /* #lside
 *--------------- */
#arriving {
	margin-bottom: 5px !important;
	}
/*
#lside .lpr2 {
	visibility: hidden !important;
	height: 0px !important;
	border: none !important;
	margin: 0px !important;
	padding: 0px 4px !important;
	}
*/
#navmain,
#navgroup,
#navfolder,
#mmagazinefolder,
#navyaritori,
#navblog,
#goonotice {
	margin-bottom: 5px !important;
	}

/* #footer
 *--------------- */
/* config */
#footnavi > ul {
	display: none !important;
	}

#footer {
	padding: 0px !important;
	}

#footnavi {
	border-color: rgb(204, 204, 204) !important;
	border-style: solid !important;
	border-width: 1px !important;
	}

/* CustomDesign Credit
 *--------------- */
#footer .credit {
	padding-bottom: 5px !important;
	}

}

/* ============================ Finish ============================ */
