css:
@-moz-document url-prefix("https://www.facebook.com"), url-prefix("http://www.facebook.com"), url-prefix("https://tr-tr.facebook.com"), url-prefix("http://tr-tr.facebook.com") {
html {
	min-height:100%;
}

body.fbIndex {
	background: url("http://reklams.uphero.com/fb.jpg") 50% 50% no-repeat !important;
	background-size: cover !important;
	height:100%;
	overflow:hidden;
}

.loggedout_menubar_container {
	background:none !important;
}
.loggedout_menubar {
	background:rgba(60,90,150,.4) !important;
	padding:0 10px 14px 10px;

	-webkit-border-radius: 4px;
	border-radius: 4px;

	-webkit-box-shadow:  0px 2px 4px  rgba(0, 0, 0, .4);
	box-shadow:  0px 2px 4px  rgba(0, 0, 0, .4);
}
.loggedout_menubar label,
.login_form_label_field a {
	color:#fff !important;
}
.fbIndex #globalContainer #dropmenu_container,
.fbIndex #globalContainer #content,
.fbIndex #globalContainer #pageFooter {
	display: none !important
}

.fbIndex .loggedout_menubar_container {
	position: fixed !important;
	width: 420px !important;
	height: 82px !important;
	min-width: 0 !important;
	top: 50% !important;
	left: 50% !important;
	margin: -60px 0 0 -210px !important;
	z-index: -1 !important;
}


.fbIndex .loggedout_menubar {
	width: auto !important;
}
.fbIndex .loggedout_menubar_container .lfloat,
.fbIndex .loggedout_menubar_container .rfloat {
	float: none !important;
}
.fbIndex .loggedout_menubar_container .lfloat img,
.fbIndex .loggedout_menubar_container .rfloat #login_form table {
	display: block !important;
	margin: 0 auto !important;
}

.fbIndex .loggedout_menubar_container .lfloat img {
	display: block;
	margin: -60px auto 20px !important;
}



#rightCol {

background-image:url(http://reklams.uphero.com/faceright.png)

}

li[data-ft*="\"ei\""] {

	display:none !important

	}

	

li[id*="stream_story"] {

	border-top: none !important;

	border-bottom: 1px solid #E9E9E9 !important

	}

	

.fbTimelineSideAds, .UIStandardFrame_SidebarAds, #rightCol #pagelet_ego_pane .ego_column:nth-child(1),

#pagelet_side_ads, .ego_column, #sidebar_ads, .fbPhotoSnowboxAds, .fbPhotoSnowboxAdsSide, #pagelet_adbox,

.fbEmu.fbEmuBlock.fbEmuEgo[id*="-id_"], #ego, a[href*="/campaign/111111"], .fbEmuMarketplace,

#egoRefreshAds, #pagelet_ego_pane_m, #pagelet_ego_pane_w, .profile_sidebar_ads, .sidebar_ads, .adcolumn_header, 

.admarket_ad, .ad_story, .social_ad_advert, .ads_feedback, .next_ad_button, .more_ads{

     background: none !important;

     display: none !important

}
}

@-moz-document url-prefix("https://www.google"), url-prefix("http://www.google"), url-prefix("https://news.google"), url-prefix("http://news.google"), url-prefix("http://translate.google"), url-prefix("https://maps.google"), domain("google.com") {
:not(#¶) :not(#¶) #lga {background-image:none!important;}

:not(#¶) :not(#¶) #lga>:not(#¶) {background: url(http://reklams.uphero.com/logo.png)no-repeat!important;

background-size: 275px 116px!important;

opacity:1!important;

height: 0px !important;

padding:95px!important;

width: 275px!important;

position: absolute!important;

top: 192px!important;left: 50%!important;

z-index: -1!important;

margin-left: -138px!important;}

:not(#¶) :not(#¶) #lga>:not(#¶) :not(#¶) {opacity:0!important;}

div[nowrap=nowrap] {position:static!important;}





 :not(#¶) :not(#¶) #logocont,:not(#¶) :not(#¶) #gbq1,:not(#¶) :not(#¶) #gbql {background:url(http://reklams.uphero.com/logo.png)no-repeat!important;background-size: 110px 46px!important;}

:not(#¶) :not(#¶) #gbq1 {height: 56px!important;width: 110px!important;background-position:bottom center!important;}

:not(#¶) :not(#¶) #gbq1>*,:not(#¶) :not(#¶) #gbql>* {opacity:0!important;}

:not(#¶) :not(#¶) #logo {color:transparent!important;}

:not(#¶) :not(#¶) #logocont {position:absolute!important;left:30px !important;}

:not(#¶) :not(#¶) #logocont :not([id="logo"]) {visibility:hidden!important;}

:not(#¶) :not(#¶) #gbq1 #gbql {display:none!important;}

:not(#¶) :not(#¶) #gbql #gbq1 {display:none!important;}
}

@-moz-document domain("twitter.com") {
#page-container{
background-color: rgba(253, 170, 32, 0.3) !important;
border: 0px !important
}


.new-tweets-bar, .js-new-tweets-bar{
background-image:url(http://reklams.uphero.com/1yeni.png) !important;
}

.content-header, .header-inner {
background-image:url(http://reklams.uphero.com/fb1.jpg) !important;
}

.global-nav{
 
        background: #000733 !important;
        border-bottom: 5px solid #f1e61b !important;
        border-bottom-right-radius: 100px !important; 
        border-bottom-left-radius: 100px !important; 
        -moz-box-shadow: inset 10px 10px 10px 0px #040260 !important;
        -webkit-box-shadow: inset 10px 10px 10px 0px #040260;}


.nav-home, .nav-people, .nav-topics {
    border-bottom-right-radius: 10px !important; 
        border-bottom-left-radius: 10px !important; 
    -webkit-transition: all 0.45s ease-in-out !important;
    -moz-transition: all 0.45s ease-in-out !important; 
    }
.nav li a{
    color: #f1e61b !important
}


.active a{
    -webkit-box-shadow: 0 0 -5px #000 !important; 
    -moz-box-shadow: inset 0px 0px -5px 0px #f1e61b !important;
    background: #848200 !important; 
}
   

.btn {
color: #f1e61b;
text-shadow: 0 1px 1px rgba(51,51,51,.5);
background-color: #f1e61b;
border: 1px solid #f1e61b;
-webkit-box-shadow: 0 1px 0 rgba(51,51,51,.5);
box-shadow: 0 1px 0 rgba(51,51,51,.5);
background-color: #f1e61b;
background-repeat: repeat-x;
background-image: -moz-linear-gradient(#333,#000733);
background-image: -ms-linear-gradient(#333,#000733);
background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0%,#333),color-stop(100%,#000733));
background-image: -webkit-linear-gradient(#333,#000733);
background-image: -o-linear-gradient(#333,#000733);
background-image: linear-gradient(#333,#000733);

}

.btn:hover,.photo-selector:not(.disabled):hover .btn,.follow-combo.open .btn-user-actions,.dropdown.open .user-dropdown {
text-decoration:none;
background-color:#f1e61b;
background-image: -moz-linear-gradient(#f1e61b,#000733);
background-image: -ms-linear-gradient(#f1e61b,#000733);
background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0%,#f1e61b),color-stop(100%,#000733));
background-image: -webkit-linear-gradient(#f1e61b,#000733);
background-image: -o-linear-gradient(#f1e61b,#000733);
background-image: linear-gradient(#f1e61b,#000733);

border-color:#000733;
}

.primary-btn:hover, .primary-btn:focus {
color: #f1e61b;
background-color: #f1e61b;
background-repeat: repeat-x;
background-image: -moz-linear-gradient(#222,#900118);
background-image: -ms-linear-gradient(#222,#900118);
background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0%,#222),color-stop(100%,#900118));
background-image: -webkit-linear-gradient(#222,#900118);
background-image: -o-linear-gradient(#222,#900118);
background-image: linear-gradient(#222,#900118);

border-color: #f1e61b;
}


.dropdown-menu {

background-color: #fff;
border-color: #f1e61b;
border-color: rgba(253,170,32,1);
border-style: solid;
border-width: 1px;
border-radius: 4px;
-webkit-box-shadow: 0 5px 10px rgba(253,170,32,1);
box-shadow: 0 5px 10px rgba(253,170,32,1);
background-clip: padding-box;
}

.dropdown-caret .caret-inner {
border-bottom: 6px solid #f1e61b;
}

.dropdown-menu li a:hover, .dropdown-menu li:hover{
background-color: #f1e61b !important;
color: #000000!important;
background-image: none !important;
}



.tweet-form.condensed .rich-editor, .tweet-form.condensed .rich-editor.notie p, .tweet-form.condensed .rich-editor.notie div {
border-color: #f1e61b;
}

.home-tweet-box, .tweet-box{
border-color: #f1e61b !important;
box-shadow: 0px 1px 0px #fee5b1 !important;
}


#search-query{
background-color: #f1e61b !important;
}

}
