css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikipedia.org") {
body {font-family:Georgia, "Times New Roman", serif !important; line-height: 1.5em !important; background:#111!important; color: #dbdbdb!important; }
#content {border: none !important; background:snow!important; color:black!important; font-size:13px !important; padding:15px !important; margin:0!important; margin-top:35px!important; }
a {color:#333!important; border-bottom: 1px dotted #777!important;}
a.image {border:none!important;}
a img {border:none!important;}
#column-content{margin:0!important;}
.pBody {background-color:transparent!important; border:none!important; padding:0!important; text-transform:lowercase;}
.infobox, .vevent,#contentSub, table.navbox, .toc, .catlinks, .metadata,.plainlinks,.mbox-small, .noprint, .tright, .portal, .infobox, .geography, .vcard{-moz-border-radius:5px !important;-webkit-border-radius:5px!important;}
.wikitable {margin:10px!important; min-width:100px;}
.summary {background-color:#DDD!important;}
.ambox {text-align: left !important; margin:0!important; border-left:10px solid #AAA!important; -moz-border-radius:5px;-webkit-border-radius:5px; margin-bottom:10px !important;}
#p-logo,#p-lang, #p-interaction, #n-contents, #footer,#p-tb, #n-currentevents, #p-personal, .editsection,#siteNotice, #p-search h5,#p-navigation h5, #siteSub, #p-search input.searchButton {display:none!important;}
.navbox-title {background:#eee!important;}
h2 {clear:left;}
#p-navigation {position:static !important; display:inline !important;}
#p-navigation ul {position:absolute !important; top:7px !important; right:230px !important; list-style-type:none !important;list-style-image:none !important;}
#p-navigation ul li {list-style-type:none !important; list-style-image:none !important; float:left; margin-left:15px!important;}
#p-navigation ul li a {border-bottom:none !important; color: #aaa !important;}
#p-cactions li a {padding:8px !important;}
#p-search {position:static!important;}
#searchform {display:block !important;}
li#ca-talk {margin-right:0 !important;}
h1#firstHeading {border:none !important; font-style:italic; font-size:2.5em !important; color:#111!important; margin-bottom:15px !important; line-height:1em !important;padding-top:5px!important;padding-bottom:0px!important;} 
#column-content {float:none !important; width:auto!important;}
#searchBody {padding:0!important; margin:0!important; border:0 !important; background:none!important;}
#bodyContent p {line-height:1.8em !important;}
input#searchInput {position:absolute!important; top:9px !important; right:10px !important; -moz-border-radius:30px!important; width:200px !important;}
#p-cactions li {border:0 !important; background:none !important; background-color:transparent !important;}
#p-cactions li a {border:0 !important; background:none !important; background-color:transparent !important; color:#dbdbdb !important; }
#p-cactions {left: -22px !important; top: 7px !important;}
#searchInputSuggest {right:0px !important;}
.dablink {background-color:#DDD!important; color:#222!important; padding:5px 10px 
!important; -moz-border-radius:5px; margin-bottom:15px !important;}
.dablink a {}
#toc {background-color:transparent !important;font-size:9px!important;}
#column-one {padding-top:0 !important;}
#contentSubs, #contentSub2s {margin:0px!important;margin-bottom:5px!important; margin-top:-10px; text-indent:0!important;font-size:10px!important;}
#contentSub, #contentSub2 {position:absolute!important; top:30px; right:15px;}
#contentSub, #contentSub a {color:#555!important;}
}
