/* â–“â–“ Modern - Wiki:                          â–“â–“
   â–“â–“ A custom userstyle for 'wikipedia.org'  â–“â–“
   â–“â–“ by vetinari - 2009                      â–“â–“
   â–“â–“ contact: vetinari.userstyles@inode.at   â–“â–“
   â–“â–“ --------------------------------------- â–“â–“
   â–“â–“ last modified: 02.07.2009               â–“â–“
   â–“â–“_________________________________________â–“â–“ */
@charset "UTF-8";
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("wikipedia.org"), domain("wikinews.org"), domain("wiktionary.org") {
/* ################### ------- overall-settings -------- ####################### */
body{background: #f4f4f4 !important}
hr{height:1px!important;color:#aaa!important; background:#aaa!important; border:0 none #aaa!important; overflow:hidden!important; padding:0!important; margin: 1px 12px!important}
#content{margin:56px auto 0px auto !important; border:1px solid #aaa !important; width:80% !important; min-width:880px !important; max-width: 1120px !important; -moz-border-radius:7px !important; background: #ffffff !important}
.portlet{width:auto !important}
fieldset{border-color:#9A3613!important}
a{color:#9A3613 !important}
a.new{color:#000 !important}
.portlet ul, .portlet ul li, #bodyContent ul{list-style-image:url(data:image/gif;base64,R0lGODlhBQAHAKIAANe4rbl3YNGqnaRKK+3k4axcQLh1XQAAACH5BAEAAAcALAAAAAAFAAcAAAMQSDZGUaKEAQ5gsLTVjv9gAgA7) !important;  color:#9A3613 !important}
#bodyContent ul li{color:#111 !important}
#column-one{padding:0 !important;position: absolute !important; top:0px !important; left: 0px !important; width: 100% !important; height: 49px !important; background: transparent url(data:image/gif;base64,R0lGODlhAQAxALMAAJSUlE9PT2lzeHB6f2p0eWt1em95fm13fG54fWx2ewAAAAAAAAAAAAAAAAAAAAAAACH5BAAAAAAALAAAAAABADEAAAQfEIlCTkoEHXuPKUhiIMhgDKSRIIXBJRtCdEQhIAEQAQA7) repeat-x top !important; z-index:555 !important}
pre{ white-space: pre-wrap !important; }
/*XXXXXXXXXXXXXXXXXXXXXXXXXXXXXXXX|||||||||||Content-TABLE-COLORING-TESTING||||||||||||||||XXXXXXXXXXXXXXXXXXXXXXXX*/
#bodyContent table[class~="float-right"] tr[style~="background:"], #bodyContent table[class~="float-right"] td[style~="background:"],#bodyContent table[class~="float-right"] tr[style~="background-color:"], #bodyContent table[class~="float-right"] td[style~="background-color:"],#bodyContent table[class~="float-right"] td[class][style=""]{background:#EEDDCC none!important; color:#000!important}
#bodyContent table.infobox td[style]{background:#E8E8E8 none!important; color:#000!important}
#bodyContent table[class~="float-right"] .navbox-group{background:#E8E8E8 none!important; color:#000!important}
table.taxobox, table.taxobox th, table.taxobox tr, table.taxobox td{border-color: #9A3613 !important}
table.wikitable, table.wikitable td, table.wikitable th, table.wikitable tr{border-color: #9A3613 !important}
table.prettytable th{border-color: #9A3613 !important}
#bodyContent table[class~="float-right"] a>font{color:#333!important}
#bodyContent table[class~="float-right"] a+font{color:#666!important}

/* ################## ------- Layout-Mainpage -------- ####################### */
#mp-upper{margin-top: 12px!important}
#mp-upper #mp-left, #mp-upper #mp-right, .MainPageBG{background: #fff!important}
#mp-upper>tbody>tr>td[style="border: 1px solid transparent;"]{border: 0 none #fff!important}
/*  --------  German.de.us/eng --------- */
#hauptseite h2, #mp-upper h2{background: #eaeaea !important}
#hauptseite *, #mp-upper *{border-color:#bbb!important}
/*  --------  Columns.de --------- */
#hauptseite-artikel .inhalt, #mp-upper #mp-left #mp-tfa{-moz-column-count: 2!important; -moz-column-gap:8px!important}
#hauptseite-ereignisse ul, #mp-upper #mp-left #mp-dyk{-moz-column-count: 2!important; -moz-column-gap:18px!important}
#hauptseite table tr+tr td:first-child{width:56%!important}
#hauptseite table tr+tr td+td{width:44%!important}
#hauptseite h2{cursor: default !important}
#hauptseite-verstorbene .inhalt, #hauptseite-wissenswertes .inhalt{display:none !important;}
#hauptseite-verstorbene:hover .inhalt, #hauptseite-wissenswertes:hover .inhalt{display:block !important;}
/*  --------  featured picture.us/eng --------- */
#mp-tfp{margin-bottom:10px!important; font-size:.85em!important; background: transparent!important}
#mp-tfp *{margin:0 !important; padding:0!important}
#mp-tfp .MainPageBG, #mp-tfp .MainPageBG>table, #mp-tfp .MainPageBG>table *{background:#444!important; color: #bcbcbc!important}
#mp-tfp .MainPageBG>table a {color: #dadada!important}
#mp-tfp .MainPageBG>table h2{border:0 !important; padding-left:16px!important}
#mp-tfp .MainPageBG>table a.image img{margin: 6px 20px 8px 20px!important; border-style: solid none none solid!important;border-width:2px 0 0 2px!important; border-color:#000!important; padding: 10px 14px 10px 12px!important; background: #333!important}
#mp-tfp p, #mp-tfp p+small, #mp-tfp div {max-width: 660px!important; margin: 0 auto!important; display:block!important}
/*  ---- Welcome to W.de.us/eng ---- */
#hauptseite-willkommen{overflow:visible !important; cursor: default !important; position: absolute !important; bottom:-15px !important; right:0px !important; z-index:999 !important; font-size: .9em !important; z-index:666 !important; width: 42% !important; margin-right:14px !important}
#hauptseite-willkommen .inhalt{ display:none !important;border:1px solid #bbb !important; position: absolute !important; bottom:19px !important;-moz-border-radius:7px !important;}
#hauptseite-willkommen:hover .inhalt{display:block !important;}
div#hauptseite-willkommen:hover h2{color:#333 !important}
#hauptseite-willkommen span{white-space: normal !important;}
div#hauptseite-willkommen h2{background: transparent!important; border: 0px none #fff!important; font-weight:normal !important; color: #999 !important}
#mp-topbanner td[style="width: 280px; text-align: center; white-space: nowrap; color: rgb(0, 0, 0);"]:before{content:"Welcome to Wikipedia / Portals";color:#999!important; font-size: 12px!important;}
#mp-topbanner:hover td[style="width: 280px; text-align: center; white-space: nowrap; color: rgb(0, 0, 0);"]:before{content:"Welcome to Wikipedia / Portals";color:#333!important; font-size: 12px!important;}
#mp-topbanner td[style="width: 280px; text-align: center; white-space: nowrap; color: rgb(0, 0, 0);"] div{display:none !important}
#mp-topbanner{overflow:visible !important; cursor: default !important; position: absolute !important; bottom:-30px !important; left:0px !important; z-index:999 !important; font-size: .9em !important; z-index:666 !important; width: 40% !important; margin-right:0px !important; border: 0!important; background: transparent!important}
#mp-topbanner td[style="width: 11%; font-size: 95%;"]{min-width:88px !important}
#mp-topbanner td[style="width: 11%; font-size: 95%;"] ul{background: #fff !important; position:relative!important; top:-40px!important; left:-275px!important; margin:0px -8px 0px -8px !important; list-style-position: inside !important;padding:0px 0px 0px 10px!important;  -moz-border-radius:7px !important;visibility:hidden!important;}
#mp-topbanner:hover td[style="width: 11%; font-size: 95%;"] ul{visibility:visible !important;}
/*  ---- sister projects.de.us/eng ---- */
div#mp-sister p{display:none !important}
div#hauptseite-schwesterprojekte,div#mp-sister{overflow:visible !important; cursor: default !important; position: absolute !important; bottom:-3px !important; right:0px !important; z-index:999 !important; width: 180px !important; font-size: .9em !important;z-index:666 !important}
div#hauptseite-schwesterprojekte .inhalt{display:none !important;  border:1px solid #bbb !important;position: absolute !important; bottom:18px !important; -moz-border-radius:7px !important; right:14px !important}
div#hauptseite-schwesterprojekte:hover .inhalt,  div#mp-sister:hover table.plainlinks{display:block !important}
div#hauptseite-schwesterprojekte h2{background: transparent!important; border: 0px none #fff!important; font-weight:normal !important; color: #999 !important}
div#hauptseite-schwesterprojekte:hover h2, div#mp-sister:hover h2{color:#333 !important}
div#mp-sister{width: 440px !important; bottom:-19px !important; right: -14px!important}
div#mp-sister table.plainlinks{display:none !important;  border:1px solid #bbb !important;position: absolute !important; bottom:28px !important; -moz-border-radius:7px !important; right:14px !important; background: #fff !important}
div#mp-sister h2{background: transparent!important; border: 0px none #fff!important; font-weight:normal !important; color: #999 !important; font-size:12px!important; text-align:center!important}

/* ################# ------- Layout-Article/Content -------- ###################### */
#bodyContent th[style], #bodyContent table[class] th{background: #eeddcc !important}
#bodyContent tr[style], #bodyContent table[style], #bodyContent table[class], #bodyContent table[class] tr,#bodyContent table[class] td{border-color: #9A3613 !important}
.navbox-abovebelow, .navbox-group, .navbox-subgroup .navbox-title {background: #e0e0e0 !important}
.navbox-subgroup .navbox-group, .navbox-subgroup .navbox-abovebelow {background: #eaeaea !important}
.navbox-even{background: #f5f5f5 !important}
#bodyContent table td.navbox-list,#bodyContent table .navbox-columns-table td{border-color: #FFFFFF !important}
#bodyContent table.gallery td{border-color: #FFFFFF !important}
/*  -------- images ----------- */
div.tright{border-left: 6px solid #fff !important}
div.tleft{border-right: 6px solid #fff !important}
/*  --------- TOC ------------- */
#bodyContent table#toc{border-color: #aaaaaa !important}
table#toc td>ul{-moz-column-count: 3!important; -moz-column-gap:6px!important;  padding: 0px  !important;}
table#toc td ul{list-style-image:none !important; list-style-type:none !important; }
/*  --------  Columns --------- */
/*  #bodyContent {-moz-column-count: 2!important; -moz-column-gap:20px!important}*/
/* ################## ---- additional menu effects ---- ######################## */
/*  ---- Document-top TABs ---- */
#p-cactions{left:auto!important; right: 16px !important; top: 26px !important; }
#p-cactions ul:before{content:"+ Document Menu" !important; color: #eaeaea; padding:0px 0px 0px 10px!important}
#p-cactions ul{-moz-border-radius: 5px!important; padding: 3px 10px 3px 3px !important; margin: 0 !important;opacity: .8!important; cursor: default; overflow: hidden!important; height: 15px !important}
#p-cactions ul:hover{background: #484450 !important; height:auto !important }
#p-cactions li{display: block !important; background: transparent !important; border: 0 none #fff!important; margin: 0!important; padding:0 !important}
#p-cactions li a {background: transparent !important; color: #ffffff !important; border-left:10px solid #484450!important; padding:0px 2px 0px 4px!important}
#p-cactions li a:hover, #p-cactions li.selected a:hover{ color:#eaeaea!important; border-left:10px solid #eaeaea!important;}
#p-cactions li.selected a { color:#FFCC33 !important; border-left:10px solid #FFCC33!important; font-weight:bold!important}
/* ########## portlets ######## */
/*  -------- WIKI-SEARCH ------- */
#p-search{ cursor: default !important; position: absolute !important; top:8px !important; right:50% !important; z-index:809 !important; overflow: visible !important; margin-right:76px !important}
#p-search h5{display:none !important}
#searchBody{-moz-border-radius:16px !important; padding: 5px 8px 4px 7px !important; background:#bbb url(data:image/gif;base64,R0lGODlhAQAjAMQAAJ2dnbq6uqKiopmZmZubm5+fn7Ozs729vbm5uampqZycnKqqqre3t5qamsTExKenp8HBwa6urru7u6urq7W1tcLCwqWlpb+/v7CwsKCgoMPDwwAAAAAAAAAAAAAAAAAAACH5BAAAAAAALAAAAAABACMAAAUd4KBk1oJRwVVpjqZV0HVICEMZWDQljyVkBQChEQIAOw==)!important; border-top:1px solid #444 !important; border-left:1px solid #444 !important; }
#searchInput{-moz-appearance:none !important;-moz-border-radius:12px !important; padding: 0px 6px 1px 6px !important; font-size: 14px !important; margin: 0px 8px 0px 2px !important; background: #fefefe !important; border-top:2px solid #666 !important; border-left:2px solid #666 !important;border-right:1px solid #ccc !important; border-bottom:1px solid #ddd !important }
#searchGoButton{-moz-appearance:none !important;-moz-border-radius:12px !important; padding: 0px 6px 0px 6px !important; font-size: 13px !important;border:1px solid #333!important;margin: 0px 3px 0px 0px !important   }
#mw-searchButton{-moz-appearance:none !important;-moz-border-radius:12px !important; padding: 0px 6px 0px 6px !important; font-size: 13px !important;border:1px solid #333!important; color:#888!important  }
#mw-searchButton:hover, #searchGoButton:hover{cursor:pointer !important;color:#9A3613 !important; border: 1px solid #777 !important}
#searchInputSuggest{ z-index:566 !important; top:39px !important; margin-left:6px !important; position: fixed !important}
/*  -------- Navigation ------- */
#p-navigation{ cursor: default !important; position: absolute !important; top:16px !important; left:50% !important; z-index:900 !important; overflow: visible !important; margin-left:60px !important}
#p-navigation div.pBody{position:relative !important; top:3px !important; left:0px !important; display:none !important;  z-index:99 !important;}
#p-navigation:hover .pBody{display:block !important}
#p-navigation:hover h5{opacity: 1!important}
#p-navigation h5{color:#FFFFFF !important;background: #a8a8a8 !important; -moz-border-radius: 9px !important; border:1px solid #e0e0e0 !important;opacity: 0.7!important; padding:0px 12px 1px 12px!important}
/*  -------- tools -------- */
#p-tb{ cursor: default !important; position: absolute !important; top:0px !important; left:80px !important; z-index:909 !important; overflow: visible !important}
#p-tb div.pBody{position:relative !important; top:0px !important; left:0px !important; display:none !important;  z-index:99 !important;}
#p-tb:hover .pBody{display:block !important}
/*  -------- partizipate -------- */
#p-Mitmachen{ cursor: default !important; position: absolute !important; top:0px !important; left:160px !important; z-index:990 !important; overflow: visible !important;}
#p-Mitmachen .pBody{display:none !important;  z-index:99 !important;}
#p-Mitmachen:hover .pBody{display:block !important}
/*  ----- Languages ----- */
#p-lang{ cursor: default !important; position: absolute !important; top:16px !important; left:50% !important; z-index:999 !important; overflow: visible !important; margin-left:160px !important}
#p-lang div.pBody{position:relative !important; top:3px !important; left:0px !important; display:none !important;  z-index:99 !important;}
#p-lang:hover .pBody{display:block !important}
#p-lang:hover .pBody ul{-moz-column-count: 3!important; -moz-column-gap:16px!important}
#p-lang:hover h5{opacity: 1!important}
#p-lang h5{color:#FFFFFF !important;background: #a8a8a8 !important; -moz-border-radius: 9px !important; border:1px solid #e0e0e0 !important;opacity: 0.7!important; padding:0px 12px 1px 12px!important}
li.interwiki-en, li.interwiki-de{font-weight:bold !important}
/*  ----- personal portlet ----- */
#p-personal{position:absolute!important; height: 10px!important; width:10px!important; -moz-border-radius: 8px!important; background: #6D777C none!important; overflow: hidden!important; top:14px!important; right:auto!important; left: 8px!important;padding: 0px !important; color: #6D777C!important; cursor: default!important; z-index:9999!important;border:3px solid #bbb!important;}
#p-personal:hover{width: auto!important; height: auto!important; padding: 8px!important;  background: #fff none!important; border:1px solid #666!important;}
li#pt-userpage, li#pt-anonuserpage, li#pt-login{background: transparent none!important; padding: 0!important}
#p-personal li{display: list-item!important; margin: 1px 3px 2px 12px!important}
#p-personal ul{text-align:left!important; padding: 2px 8px!important; margin: 20px 0 0 0!important}
#p-personal:hover ul {margin:4px 0 0 0!important}
#p-personal li a{color:#9A3613!important}
#p-personal li a:hover{text-decoration:underline!important}
/* ################## ----- Elements_display_none ----- ####################### */
#siteNotice, #p-logo, #footer, #anon-banner, .metadata, #mp-lang, #mp-other, #mp-strapline, .portlet, #searchBody>div>span{display:none !important}
#p-navigation, #p-lang, #p-search, #p-cactions, #p-personal{display:block !important}
}
/* ################## ----- Startpages/ language-versions ----- ####################### */
@-moz-document url-prefix(http://de.wikipedia.org/wiki/Wikipedia:Hauptseite), url-prefix(http://de.wikipedia.org/wiki/Hauptseite), url-prefix(http://en.wikipedia.org/wiki/Main_Page), url-prefix(http://it.wikipedia.org/wiki/Pagina_principale), url-prefix(http://fr.wikipedia.org/wiki/Accueil),  url-prefix(http://es.wikipedia.org/wiki/Wikipedia:Portada), url-prefix(http://sv.wikipedia.org/wiki/Portal:Huvudsida),  url-prefix(http://no.wikipedia.org/wiki/Hovedside)
{
#content{margin:128px auto 0px auto !important; border:1px solid #aaa !important; width:84% !important; min-width:880px !important; max-width: 1120px !important; -moz-border-radius:7px !important; background: #ffffff !important}
#p-logo{display:block !important; width:140px !important; position: absolute !important; left:50% !important; margin-left:-80px !important; top: 49px !important; height:76px !important; overflow:hidden !important;}
#p-logo a{ background-position:bottom !important; height:76px !important;}
#content *{-moz-border-radius:0px!important}
#content th div , #content th h2{background:#eaeaea none!important; border-color:#bbb!important }
#content th {background:#FFFFFF none!important; border:0px none #bbb!important}
#content td, #content tr, #content table {background:transparent none!important; border-color:#bbb!important}
#content tbody>tr>td[style="border: 1px solid transparent;"]{border: 0 none #fff!important}
#bodyContent div{background:transparent none!important; border-color:#bbb!important}
}
@-moz-document url-prefix(http://it.wikipedia.org/wiki/Pagina_principale){
#bodyContent div h2{background:#eaeaea none!important; border-color:#bbb!important }
}
