css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("anond.hatelabo.jp") {
/* ---------------------------------------------------------------------
  reset    
---------------------------------------------------------------------- */
/* --------------------
  hatena-body    
-------------------- */
#body form{
  margin:0 !important;
}
div.hatena-body{
  margin:0 !important;
  width:100% !important;
}
div.body{
  margin:0 !important;
  padding:0 !important;
  border:none !important;
  font-size:85% !important;
}
div.section{
  margin-bottom:0 !important;
  margin-top:0 !important;
}
div.section p{
  padding-bottom:0 !important;
  padding-top:0 !important;
  background-color:#FCFCEB !important;
}
div.section p.sectionfooter{
  font-size:95% !important;
  margin-top:0 !important;
}
p{
  margin-left:0 !important;
  text-indent:0 !important;
  line-height:1.5 !important;
  text-align: justify;
}
blockquote{
  margin:0 !important;
  padding:0 !important;
  border:none !important;
  font-size:100% !important;
}
pre{
  margin:0 !important;
  padding:0 !important;
  background-color:#FCFCEB !important;
  font-size:100% !important;
}
table{
  margin:0 !important;
  border:0 !important;
  font-size:100% !important;
}
table td{
  border:none !important;
}
table th{
  background-color:#FCFCEB !important;
  border:none !important;
}


/* --------------------
  h1-    
-------------------- */
h1,h4{
  border:none !important;
}
h1,h2{
margin:0 !important;
padding-top:0 !important;
padding:0 !important;
}
h2{
  padding:0 !important;
  background-color:#FCFCEB !important;
}


/* --------------------
  trackback    
-------------------- */
.caption{
  border:none !important;
}
.refererlist ul{
  padding:0 !important;
  background-color:#FCFCEB !important;
}
.box-curve{
  margin:0 !important;
  padding:0 !important;
  width:100% !important;
}


/* --------------------
  a    
-------------------- */
#globalheader div#bannersub table td a{
  color:inherit !important;
}

a.keyword{
  border:none !important;
  color:inherit !important;
}

/* --------------------
  display    
-------------------- */
#headtable,
h1 img,
#intro,
.pager-r a,
.pager,
.afc,
.sanchor,
div.navigation{
  display:none !important;
}

/* ---------------------------------------------------------------------
  hatena4    
---------------------------------------------------------------------- */
/* --------------------
  a    
-------------------- */
a:link{
  color:inherit !important;
}
a:visited{
  color:#848A81 !important;
}

/* --------------------
  header    
-------------------- */
div#bannersub table td{
  padding-top:10px !important;
  background-color:#FCFCEB  !important;
  color:#2E322B !important;
}
div#bannersub table td.gmenu a{
  font-weight:bold;
  text-decoration:none !important;
  text-shadow: 0px 1px 0px #ccc;
}
#globalheader div#bannersub table td.gmenu{
  padding-bottom: 5px !important;
  background-color:#F2F2B0 !important;
}
#globalheader div#bannersub table td.username{
  padding-left:0 !important;
}
td.username + td.gmenu{
  -moz-border-radius-bottomleft:5px;
}
td.username + td.gmenu +td.gmenu + td.gmenu{
  -moz-border-radius-bottomright:5px;
}
#globalheader div#bannersub table td.gmenu:hover{
  background-color:#B6B684 !important;
}

body{
  width:65% !important;
  margin:0 auto !important;
  color:#2E322B !important;
  background-color:#5B6356 !important;
}
#globalheader,
h1,#breadcrumbs{
  padding:0 3% !important;
  background-color:#FCFCEB  !important;
  color:#2E322B !important;
}
#breadcrumbs{
  margin:0 !important;
  padding-bottom:10px !important;
  font-size:80% !importnat;
}
#intro + .pager-l{
  display:none !important;
}
#body{
  padding:0 3% !important;
  background:#FCFCEB !important;
  line-height:nomal !important;
}
#footer{
  background:#FCFCEB !important;
  color:#2E322B !important;
}
#text-body,#text-title{
  border:1px solid #2E322B !important;
}
.post-submit{
  padding-bottom:40px;
}

/* --------------------
  h1-    
-------------------- */
h1{
  padding:30px 3% !important;
  letter-spacing:2px;
  font-weight:lighter !important;
}
h2 a,
span.date{
  font-size:40pt !important;
  font-family:Georgia,serif;
  color:#E4F0CC !important;
  text-decoration:none !important;
}
h3{
  background-color:#E4F0CC !important;
}
h3:before{
  margin-left:-0.85em;
  margin-right:10px;
  content:"â†’";
  color:#5B6356;
  font-size:40pt;
  opacity:0.80;
  text-shadow: 5px 5px 5px #444A41;
}


.pager-l a, .pager-l strong,.pager-r{
  color:#FCFCEB  !important;
}

.refererlist{
  margin:20px 0 10px 0 !important;
}
.refererlist li a{
  width:100% !important;
  background-color:#F2F8E6 !important;
}
.caption{
  margin-bottom:10px !important;
}
}
