/*Coded by Paulfox April 19th 2006, Updated 1/25/07 Ver 2.3 to GoogleImages+Mouseover(s) grey font*/
/*IMG Search Blk BG*/
@-moz-document domain(images.google.com) {body,[id*="tDataImage"],[id*="div_hidden"],[id*="div_image"],[id*="div_snippet"] {background:#000000!important} a,font {color:grey!important}}
@-moz-document domain(flickr.com),domain(images.search.yahoo.com),url-prefix(http://www.alltheweb.com/search?cat=img),url-prefix(http://www.dogpile.com/info.dogpl/search/images/),url-prefix(http://www.google.com/images),url-prefix(http://images.ask.com/pictures),url-prefix(http://pictures.ask.com/),url-prefix(http://msxml.webcrawler.com/info.wbcrwl/search/images),url-prefix(http://www.search.com/images) {
body {background:#000000!important}
a,font {color:grey!important} [src*="atwlogo.gif"],[src*="/images/images_res.gif"] {display:none!important}}
@-moz-document url-prefix(http://www.altavista.com/image/) {body,tr[class="resultGroup"] {background:#000000!important}}
@-moz-document url-prefix(http://photobucket.com/images/) {body,#containerContent {background:#000000!important}}
@-moz-document domain(search.live.com) {div[class="twg_headerhtml"] {display:none!important}
#results_control,#root,[id="scroll_panel"] {background:#000000!important}
p {color:grey!important}}
@-moz-document domain(picsearch.com) {body,[class="w"],[class="yl"] {background:#000000!important}}
