css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("4chan.org") {
body
{ background: url(http://img580.imageshack.us/img580/8980/emmaleggingsside22.jpg) no-repeat fixed !important; }
.reply, .replyhl
{ background: rgba(0,0,0,.5) !important;
color: #ffffff !important;
border: none !important;}

blockquote {color:white !important;}

span {background: none !important;}

th, tr, tbody, table {
background: none !important;}









	* { font: 10pt "calibri"; text-shadow: #000000 0px 0px 2px, #000000 1px 1px 1px;}

	a { color: #ffffff !important; text-shadow: #000000 0px 0px 2px, #000000 1px 1px 1px;}

	hr { display: none !important; }

	#navtop, #navbot { background: #000; color: #FFF !important; width: 100%; text-align: center; height: 12pt; font-size: 7pt !important; }

	#navtop *, #navbot * { color: #000; font-size: 7pt; display: inline; }

	#navtop a:hover, #navbot a:hover { background: #555; }

	form { margin: 1em; }

	.inputtext, textarea { width: 295px; }

	.rules { display: none; }

	td.postblock { background: #fff !important; border: 1px solid #fff !important; color: #000 !important; }

	td.postblock * { font-size: 6pt; }

        td.reply, td.replyhl { background: none !important; border: 1px solid #FFFFFF !important; color: #333 !important;-moz-border-radius: 5px; }

 	
	td.replyhl { background-color: #202020 !important; border: 1px solid #fff !important;	color: #fff !important; }

	.postername, .filesize, span.filetitle, .filesize a, a.linkmail, a.quotejs, .replytitle, .commentpostername, .omittedposts, .abbr { color: #fff !important; font-size: 8pt;}

	span.filetitle, .replytitle { color: #FF0000 !important; font: 10pt "calibri"; }

	#hd, small, font[size="1"], font[size="2"], #ft, #filter-button, #option-button, #navtopr, #navbotr, .logo,.logo font b span, .doubledash, img.rotating, input[type="button"], td[align="right"] { display: none !important; }

	.pages { background: none !important; border: 1px solid #000 !important; }

	.pages td { color: #ffffff !important; font-size: 4pt; }

	.pages td a, .pages td b { color: #555 !important; font-size: 8pt; }

	.pages td b { background-color: #555; }

	.pages input { display: none !important; }

	.pages td:first-child { display: none;}

	pre, textarea, input:not([type="button"]):not([type="checkbox"]):not([type="submit"]):not([type="reset"]), select { font-size: 9pt ! important; color: #fff !important; background: #333 !important; border-color: gray !important; }

	input[type="submit"] { background: #333 !important; color: #fff !important; border: 1px solid gray; -moz-appearance: none; font-size: 9pt; }

	th { background: none !important; }

        id {color:#ffffff !important;}
     
	th font { font-size: 7pt; }

	input[type="file"] { opacity: 0.12 !important; }

	td.deletebuttons { font-size: 8pt !important; color:#eee !important; }

}
