css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.last.fm")
{
    body
    {
        background: url("http://img7.imageshack.us/img7/3381/indexfx.jpg") !important;
    }

    #LastFooter
    {
        display: none !important;
    }

    #secondaryNavigation
    {
        position: relative !important;
        display: inline !important;
        width: 100% !important;
        left: 0 !important;
        top: 0 !important;  
    }

    #secondaryNavigation ul
    {
         border: none !important;
         padding-top: 4px !important;
         background: white !important;
    }

    #secondaryNavigation li
    {
        display: inline  !important;
        border-left: 1px solid #ccc !important;
        margin: 0 !important;
        padding: 0 !important;
    }

    #secondaryNavigation li a
    {
        font-size: 12px !important;
        display: inline !important;
        margin: 0 !important;
        padding: 2px 5px 4px 6px !important;
        background: none !important;
        border: 5px !important;
        color: black !important;
    }
    
    #secondaryNavigation li.first
    {
        border-left: none !important;
    }
    
    #secondaryNavigation li.current a
    {
        background: none !important;
    }

    #secondaryNavigation li a:hover
    {
        color: #092d3d !important;
        text-decoration: underline !important;
        background: none !important;
    }

    div.sic #content, div.fiflufi #content
    {
        margin: 0 50px !important;
        border-left: none !important;
        border-top: none !important;
        border-bottom: none !important;
    }

    #tagHead
    {
         width: 100% !important;
         margin: -1px -1px 0 0px !important;
    }

    .r-tag .withHead #playerReplacement
    {
         margin-top: -280px !important;
    }
   
    .betaMessageBlock
    {
         display: none !important;
    }
}
