css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("wikipedia.org"),domain("secure.wikimedia.org") {
/* ----------------------------------------------------------------------------
 Last Modified : 2011/09/25
 Author        : Qurage                                                        
 Contact       : Qurage (Qurage) on Twitter http://twitter.com/Qurage          
                                           
 --------------------------------------------------------------------------- */

body
		{
		color: #111 !important;
		background-image: none !important;
		background-color: #eff1f1 !important;
		text-align: justify !important;
		}

	 h1
	,h2
	,h3
	,h4
	,h5
	,h6
		{
		margin-bottom: 0.5em !important;
		color: #111 !important;
		line-height: 1.2 !important;
		}

	h1
		{
		margin-bottom: 0.7em !important;
		padding: 0.3em 0.3em 0.1em 2px !important;
		font-size: 400% !important;
		border-bottom: 1px dotted  !important;
		}

	h2
		{
		margin-top: 1.5em !important;
		margin-bottom: 1em !important;
		padding-left: 0.1em !important;
		font-size: 250% !important;
		border-bottom: 3px dotted #111 !important;
		}

	h3
		{
		margin-top: 1.5em !important;
		margin-bottom: 0.5em !important;
		font-size: 150% !important;
		border-left: 8px solid #111 !important;
		padding: 0.5em 0.6em 0.5em !important;
		}

	h4
		{
		font-size: 120% !important;
		}

	h5
		{
		font-size: 100% !important;
		}

	h6
		{
		font-size: 100% !important;
		}

	a
		{
		padding-left: 0.2em !important;
		padding-right: 0.2em !important;
		}

	a:link
		{
		color: #0082c8 !important;
		}

	a:visited
		{
		color: #7caaa6 !important;
		}

	a:hover
		{
		color: #fff !important;
		background-color: #008fe8 !important;
		text-decoration: none !important;
		}

	p
		{
		margin: 0.4em 0 2em !important;
		line-height: 1.6 !important;
		}



	 #content
		{
		padding: 0 3em 3em !important;
		}

	 #content > #siteNotice
	,#content > h1
	,#content > #bodyContent
		{
		max-width: 1100px !important;
		margin: auto !important;
		}

	#content > h1
		{
		margin-bottom: 1em !important;
		}

	#content > #siteNotice > #centralNotice > .notice-all
		{
		margin-top: 1em !important;
		}

	 li#ca-nstab-main      
	,li#ca-talk            
	,li#ca-view            
	,li#ca-edit            
	,li#ca-history         
		{
		margin: 0 0.5em 1em 0 !important;
		padding: 0 !important;
		}

	 #toc 
	,.toc 
	,.mw-warning
		{
		padding: 1.5em 1.3em 1em !important;
		}

	#toctitle
		{
		text-align: left !important;
		padding-bottom: 1em !important;
		}

	.thumb 
		{
		margin: 1em 2em !important;
		}

	.thumbcaption
		{
		margin: 0.8em 0 !important;
		}

	table.ambox
		{
		margin: 0 !important;
		}

	table.ambox td
		{
		padding: 2em 0 !important;
		}

	#p-cactions 
		{
		width: 100% !important;
		left: 0 !important;
		margin: 0 !important;
		padding: 0 !important;
		text-align: right !important;
		}
	#p-cactions > .pBody
		{
		padding-right: 1.3em !important;
		}



	 #content
		{
		border: none !important;
		background-color: transparent !important;
		background-image: none !important;
		}

	#content > #siteNotice > #centralNotice > .notice-all
		{
		background-color: #fff !important;
		border: none !important;
		}

	div#p-logo a:hover
		{
		background-color: inherit !important;
		}

	 li#ca-nstab-main      
	,li#ca-nstab-main a
	,li#ca-talk            
	,li#ca-talk a
	,li#ca-view            
	,li#ca-view a
	,li#ca-edit            
	,li#ca-edit a
	,li#ca-history         
	,li#ca-history a
		{
		border: none !important;
		background-color: transparent !important;
		}

	.portlet > .pBody
		{
		background-color: transparent !important;
		border: none !important;
		}

	 #toc 
	,.toc 
	,.mw-warning
		{
		background-color: #fff;
		border: none !important;
		}

	#toc h2
		{
		font-size: 120% !important;
		border: none !important;
		}

	#toctitle
		{
		text-align: left !important;
		}

	.thumb 
		{
		background-color: transparent !important;
		border: none !important;
		}

	.thumbinner
		{
		border: none !important;
		background-color: transparent !important;
		}




	 .vectorTabs
	,.vectorTabs *
	,#head-base
 		{
 		background-image: none !important;
 		background-color: transparent !important;
 		}

	 div.vectorTabs li a
	,div.vectorTabs li a span
		{
		color:#0082c8 !important;
		}

	 div.vectorTabs li.selected a
	,div.vectorTabs li.selected a span
		{
		color:#000 !important;
		}

/* ------------------------------------------------------------------------- */
}
