css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("portal.utoronto.ca") {

body {
background: #FFF !important;
}

#topFrame, .bgBanner {
background: #3F7691 !important;
}

.nav li img {
display: none;
}

.nav li a {
background: none !important;
}

#appTabList {
position: absolute !important;
}

#appTabList tbody tr td a {
background: none !important;
-moz-border-radius: 0px !important;
-webkit-border-radius: 0px !important;
border: none !important;
padding: 10px;
}

#breadcrumbs .path {
margin: 10px 0px 0px 0px !important;
background: none !important;
}

.frameResizeToggle {
display: none;
}

.topRound {
display: none;
}

.bottomRound {
display: none;
}

.localViewToggle {
background: none !important;
}

#pageTitleDiv {
display: none !important;
/*background: none !important;*/

}

#titleicon {
display: none;
}

#announcementList .clearfix {
width: 700px;
margin: 20px !important;
}

#announcementList .clearfix h3 {
background: #EDF3F6 !important;
color: #595959 !important;
border-bottom: 1px solid #BFBFBF !important;
width: 55%;
padding: 7px 42.5% 7px 2.5% !important;
font-size: 120% !important;
}


#announcementList .clearfix .details {
background: #FCFCFC;
width: 92%;
border-bottom: 1px solid #BFBFBF !important;
padding: 4% !important;
}

#announcementList .clearfix .details p {
padding: 0px 0px 18px 0px !important;
line-height: 18px;
font-size: 110% !important;
}

#announcementList li.clearfix div.announcementInfo {
width: 100% !important;
background: none !important;
border: none !important;
}

#announcementList .clearfix .announcementInfo span {
display: none;
}

#announcementList .clearfix .announcementInfo p {
visibility: visible;
float: right;
padding: 7px 10px !important;
color: #8D8D8D;
}

#puller {
display: none;
}

#courseMenuPalette_actionbar {
display: none !important;
}

.actionBarMicro {
background: none !important;
}

}
