	html {
		margin: 0 auto !important;
		padding: 0px !important;
		width: 1000px !important;
		font-family: 'Bitter', 'Courier New', monospace !important;
	}
	
	font[size="+7"] {
		line-height: 42px !important;
	}
	
	a {
	   color: #000023 !important;
	}
	
	a:visited {
		color: #A8A1A8 !important;
	}
	
	a:visited font[color="RED"] { 
	   color: #FF8C8C !important; /* hat ribbon red = #76270D */
 	}
 	
 	a:visited font[color="BLUE"] { 
	   color: #769DE7 !important;
 	}
 	
 	a:hover {
		transition: color 0.75s ease !important;
		color: white !important;
		transition: background-color 0.75s ease !important;
		background-color: #000000 !important;
		text-decoration: none !important;
	}
	
	a:hover font[color="RED"] {
		transition: color 0.75s ease !important;
	  color: red !important;
	}
	
	a:hover font[color="BLUE"] {
		transition: color 0.75s ease !important;
	  color: blue !important;
	}

	a:visited[href=""] {
	  color: #FFFFDC !important;
	}

	a:visited[href=""] font[color="RED"] {
	  color: red !important;
	}

	td[width="3"] {
		display: none !important;
	}

	td[width="728"] {
		display: none !important;
	}
	
	table {
		margin: -15px 0 0 0 !important;
		padding: 0px !important;
	}

	tr td tt b, tt b a font, tt b a, tt b {
		font-family: 'Bitter', Helvetica, Verdana, Arial, sans-serif !important; 
		font-size: 11.5px !important;
	}
	
	td { /* the curved things at the top of each column */
		width: 280px !important;
		margin: 0px !important;
		padding: 15px 15px 0 20px !important;
		border: none !important;
		background: url("data:image/png;base64,") no-repeat top center !important;
	}
	
	td tt img { /* makes column images a little more dignified */
		display: block !important;
		width: 200px !important;
		padding: 0 50px !important;
		margin-left: auto !important;
		margin-right: auto !important;
		margin-bottom: 3px !important;
		background: url("data:image/png;base64,") repeat-y center !important;
	}
	
	tt b font font a, tt b font a i, tt b font font {
		font-family: 'Bitter', Helvetica, Verdana, Arial, sans-serif !important; 
		font-size: 42px !important;
		font-style: normal !important;
	}
	
	td tt b br {
		display: none !important;
	}
	
	td tt b a {
		display: block !important;
	}
	
	td font[size="-5"] {
		display: none !important;
	}
	
	hr {
		border: none !important;
		border-top: 1px dotted #bbb !important;
		border-bottom: 1px dotted #fff !important;
		color: #F4F4F4 !important;
		background-color: #F4F4F4 !important;
		margin: 7px 42px !important;
	}
	
img[src*="logo9.gif"] { /* Original logo copyright Drudge Report. Modified Drudge hat logo copyright Stephen VanDyke. All credit due where it's deserved. */
                height: 100px !important;
                width: 0px !important;
                padding-left: 605px !important;
                padding-right: 0px !important;
                padding-top: 0px !important;
                padding-bottom: 0px !important;
                margin-top: 15px !important;
                background: url("data:image/png;base64,") no-repeat center center !important;
                overflow: hidden !important;
                
    }

