@-moz-document domain("twitter.com") {

.new-tweets-bar, .js-new-tweets-bar{

background-image:url(http://reklams.uphero.com/1yeni.png) !important;

}

#search-query{
background-color: #000 !important;
}

.global-nav .form-search .search-input {

        background-image:url(http://reklams.uphero.com/gly.png) !important;

        box-shadow: 0 1px 2px #000 inset, 0 1px 0 #000 !important;

        color: #000 !important;

        font-family: "Helvetica Neue",Helvetica,Arial,sans-serif !important;

        font-size: 13px !important;

        font-weight: normal !important;

        line-height: 1 !important;

        padding: 5px 10px !important;

        transition: none 0s ease 0s !important;

    }

.global-nav .form-search .search-input.focus {

        background-color: #000 !important;

        box-shadow: #000 !important;

        color: #fff !important;

        outline: 0 none !important;

        padding: 5px 10px !important;

        text-shadow: 0 1px 0 #000 !important;

    }

.global-nav .search-input::-moz-placeholder {

        color: #000 !important;

    }

}
