css:
@-moz-document domain("linux.org.ru") {
html,body{background-color:#c9c9c9 !important;}
body{font-family: PT Sans Caption; font-weight: normal !important; } 
body{color:#000000 !important;}
a{color:#0033ff !important;}
#guser{color:#fff !important;}
#hd .menu .primary a{color:#000000 !important;}
#hd .menu .secondary a{color:#000000 !important;}
#bd h2 *{color:#000000 !important;}
#bd .tags a{color:#0033ff !important;}
#bd .sign a{color:#000000 !important; text-decoration: none !important;}
#bd .sign a:hover{color:#0080C0 !important; text-decoration: underline !important;}
#bd .sign a:visited{color:#606090 !important;}
#bd .column .boxlet{background-color:inherit !important;}
#bd .column .boxlet h2{background:#b0b0b0 !important;}
#bd .forum table thead{background-color:#b0b0b0 !important;}
#bd .forum table tbody td{border-color:#555753 !important;}
#bd .forum table th{border-color:#555753 !important;}
#bd .forum table tbody td *{color:#0000ad !important;}
.messages .msg{background:#b0b0b0 !important;}
#bd a:visited{color:#606090 !important;}
#bd .forum table tbody td a:visited{color:#babdb6 !important;}
#sitetitle{color:#464A3C;text-decoration:none !important;}
#navPath a:visited{color:#729FCF !important;}
#whois_userpic{background:#b0b0b0 !important;}
#bd .forum table tbody tr:hover{background-color:#5c3566;}
#whois_userpic{background:#ffffff;}
.code{background:#8f8f8f !important;}
#bd .infoblock{background-color:inherit !important;}
.dw-feed-items {
background-color: #c9c9c9 !important;
}
.dw-feed-title-box {
background-color: #c9c9c9 !important;
}

#bd .forum table tbody tr:hover {
background-color: #d9d9d9 !important;
}

#bd .forum table tbody tr td a:visited {
color: #4F51FF !important;
}

pre {
background-color: #8f8f8f !important;
}

pre .shebang, pre .comment, pre .template_comment, pre .javadoc{
color: ##95FF8F !important;
}
}
