css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(vandale.nl) {

#articleMenu {
display: none;
}
#menuBar {
position:inherit !important;
height: 0px !important;
}
.menu, .menuBackground {
height: auto;
}
.teaser {
display: block;
height: auto !important;
position: absolute !important;
top: 0px;
width: 860px !important;
}
.header {
padding-left: 76px !important;
}
#searchResults .teaser {
display: none;
}
.teaserText {
display: none;
}

#srColumnSet {}
.srLeftColumn {
float: none !important;
margin-right: 0 !important;
}
.srRightColumn {
display: none;
width: auto !important;
}
#freeDictionaryWidget {
height: auto !important;
}
.contextRange {
padding-top:5px !important;
padding-bottom:5px !important;
}
.contextRange h6 {
display: none;
}
.formRow {
margin-bottom: 0px !important;
}
.submitRow {
text-align: right !important;
}

#results {
clear:none !important;
margin-top: -10px !important;
}
#results > div > br {
display: none;
}
.query {
display:none;
}
.query:first-child {
display:block !important;
}
.query > strong {
padding: 2px 5px;
background: #EAEAEA;

}
.query > br {
display: block !important;
}

.pnn4web_b {
margin-top: 1em !important;
}
.pnn4web_c { /* definition container */
font-size: 14px !important;
line-height: 17px !important;
padding-left: 30px !important;
}
.pnn4web_i {
display: none;
}
.pnn4web_i:before {
content: "\25CF";
margin-left: 2px !important;
}
.pnn4web_h, .pnn4web_i { /* definition number */
color: #FF8000 !important;
font-size: 12px !important;
line-height: 17px !important;
margin-left: -20px;
display: block;
float: left;
margin-right: 5px;
font-weight: bold;
text-align: right;
}
.pnn4web_c > span:first-child {
margin-left: -20px !important;
}
.pnn4web_d { /* word block */
font-size: 17px !important;
line-height: 17px !important;
margin: 0 5px 0 0;
padding: 2px 1px 2px 5px;
background: #EAEAEA;
font-weight: bold;
}
.pnn4web_k {
font-style: italic;
}
.pnn4web_e { /* subdefinition number */
display: block;
padding-top: 4px;
margin-bottom: -18px;
margin-left: -36px;
width: 20px;
font-size: 11px;
line-height: 17px;
font-weight: normal !important;
text-align: right;
}

}
