css:
#main-window[stylish-domain$="facepunch.com"] #browser-bottombox
  { padding-top: 1200px !important;
     margin-top: -1200px !important;}

@-moz-document domain("facepunch.com") {
/*In theory, this works. I haven't tested it extensively. The only bugs I've found so far (which I don't think I can fix) is a way to make your own posts tint a different color. Probably won't happen until I learn CSS better.*/

html, body
{ background: #000 !important; }

html > body > img[src$=".gif"]:only-of-type
  { image-rendering: optimizeSpeed !important; }
html > body > img:only-of-type
  { margin: auto;
    position: absolute;
    top: 0px; bottom: 0px; left: 0px; right: 0px; }
BODY > PRE
  { white-space: pre-wrap !important;
    word-wrap: break-word !important; }

html, #header
{
max-width: 800px !important;
margin-left: auto;
margin-right: auto;
}

.logo img, .rules, #footer center, 
div.postarea>form>div,
tbody>tr>td>small
  { display: none !important; }

html, body, blockquote, font, b, td, 
.filesize, .filetitle, .omittedposts, .abbr, .inputtext,
#header, #footer, #navbot, #navbotr
{
font-family: "Lucida Grande", sans-serif !important;
font-size: 8pt !important;
color: #fff !important;
text-shadow:0px 1px 1px #000 !important;
}

small
{
font-size: 100% !important;
}

a
{
color: #8ad2ff !important;
text-decoration: none !important;
}

a:hover
{
text-decoration:underline !important;
}

hr
{
display: none !important;
}

th,
.reply, .replyhl, .postblock, .pages, .inputtext,
#navtop, #navbot, INPUT[type="file"] > input[type="text"]
{
-moz-appearance: none !important;
background: rgba(0,0,0,.5) !important;
border: 0px #000 solid !important;
-moz-border-radius: 5px !important;
}

INPUT[type="file"] > input[type="text"]
{width: 276px !important; }


INPUT[type="button"], input[value="Delete"], input[value="Previous"], input[value="Next"]
{opacity: .99 !important; }


.doubledash
{
margin-right: 5px !important;
font-weight: bold !important;
}

.reply, .replyhl
{
width: 100% !important;
padding-bottom: 5px !important;
padding-top: 5px !important;
padding-right: 0px !important;
}

.replyhl
{
border: 1px solid #fff !important;
}

.replyhl blockquote
{
font-weight: bold !important;
}

form a img
{
-moz-border-radius: 2px !important;
border: 1px #fff solid !important;
-moz-box-shadow: 0px 1px 5px #000000 !important;
margin-top: 5px !important;
margin-bottom: 5px !important;
}

form a img:hover
{
border: 1px #8ad2ff solid !important;
}

.commentpostername, .postername, .postertrip, .replytitle
{
background: none !important;
}

.commentpostername, .postername
{
color: #ff9600 !important;
}

.postertrip
{
color: #ff00cc !important;
}

.unkfunc
{
color: #54ff00 !important;
}

.filetitle, .replytitle
{
color: #fff !important;
}

.pages
{
margin-right: 5px !important;
padding: 5px !important;
}

.logo b span
{
text-shadow:0px 1px 1px #777777 !important;
font-size: 24pt !important;
font-family: Helvetica, sans-serif !important;
font-weight: bold !important;
letter-spacing: 1px !important;
color: #000 !important;
opacity: 0.7 !important;
margin-bottom: 15px !important;
}

#navtop
{
margin-top: 5px !important;
}

#navtop, #navbot
{
max-width: 790px !important;
padding: 5px !important;
text-align: center !important;
}

#navbotr
{
margin-top: 5px !important;
margin-bottom: 5px !important;
}

#header
{
top: 0px !important;
left: 0px !important;
right: 0px !important;
}

th
{
display: block !important;
padding: 5px !important;
margin-bottom: 5px !important;
max-width: 790px !important;
}

.postarea>
form
{
margin-bottom: 5px !important;
}

div.postarea>
form>
table>
tbody>
tr>
td>
postarea.inputtext,
div.postarea>
form>
table>
tbody>
tr>
td>
textarea.inputtext
{
margin-left: 5px !important;
position: inherit !important;
height: inherit !important;
padding-top: 3px !important;
}

.inputtext
{
background: rgba(0,0,0,.5) !important;
}

input.inputtext
{
padding-top: 3px !important;
}

.postarea
{
padding-left: 0px !important;
}

.postblock
{
text-align: center !important;
}

tbody>
tr>
td>
input
{
margin-left: 5px !important;
}

.postarea>
form>
table
{
margin-right: auto !important;
margin-left: auto !important;
max-width: 400px !important;
}

form>
table>
tbody>
tr>
td>
textarea.inputtext,
div.postarea>
form>
table>
tbody>
tr>
td>
input.inputtext
{
width: 350px !important;
}

div.postarea>
form>
table>
tbody>
tr>
td>
input
{
width: 350px !important;
opacity: .99 !important;
}


/* MAIN PAGE */

#logo, .fill
{
display: none !important;
}

.top-box
{
margin-top: 5px !important;
}

.boxcontent,
#ft, #whatis, #copyright,
h2
{ 
font-size: 100% !important;
}

.box-outer, #ft ul, .bd
{
background: rgba(0,0,0,.5) !important;
border: 0px #000 solid !important;
-moz-border-radius: 5px !important;
}

.boxbar
{
background-color: transparent !important;
color: #fff !important;
}

#recent-images a
{
border: 1px #fff solid !important;
-moz-border-radius: 2px !important;
}

#recent-images a:hover
{
border: 1px #8ad2ff solid !important;
}

.right-box
{
color: #fff !important;
}

#ft li
{
background-color: transparent !important;
border: none !important;
float: none !important;
display: inline !important; 
}

#ft ul
{
padding-top: 5px !important;
padding-bottom: 5px !important;
text-align: center !important;
}

.bd
{
padding: 5px !important;
border: 1px #fff solid !important;
}

#options-container a:hover, .yuimenuitemlabel-selected, #filter-container a:hover 
{
background-color: transparent !important;
}

#options-container, #filter-container
{
background: none !important;
}

.menubutton a
{
padding-right: 0px !important;
padding-top: 0px !important;
padding-bottom: 0px !important;
padding-left: 0px !important;
}

.boxcontent
{
line-height: 120% !important;
}

.yuimenu ul
{
border: 0px #fff solid !important;
}

.yui-tt
{
color: #fff !important;
}

.boxcontent, dt
{
color: #fff !important;
}

.boxcontent dd, .boxcontent>
hr
{
background-color: transparent !important;
border-bottom: 1px #fff dotted !important;
}

.boxcontent > ul { 
color: #fff !important;
}
}
