css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.google.com/"), url("http://www.google.co.uk/"),
  url-prefix("http://www.google.com/search"), url-prefix("http://www.google.co.uk/search")
{
  /* Remove left navigation bar and shift search results */
  #leftnav { display: none !important; }
  #center_col, #foot { margin-left: 32pt !important; width: 100% !important; }

  /* Reduce font sizes of search box and buttons */
  input.lsb { font-size: 9pt !important; }
  input.lst { font-size: 12pt !important; }

  /* Hide related searches list at the bottom */
  #brs { display: none !important; }

  /* Hide various advertising sections at the bottom */
  #bottomads, .booksbox, .rbt { display: none !important; }

  /* This will hide "Related News" links.
   * However, it breaks search results for some non-US versions,
   * and is likely to cause problems in the future. Enable at your own risk.
   */
  /*
  li.g { display: none !important; }
  li.g[class~=w0] { display: block !important; }
  */
}
