css:
    @namespace url(http://www.w3.org/1999/xhtml);

    @-moz-document domain("fromthepavilion.org") {

    #banner {
       height: 63px !important;
       width: 960px !important;
               background: url(http://i970.photobucket.com/albums/ae187/nipsadinio/banner1234.jpg) no-repeat !important;
    }

    #content{
    font-size:11px!important;
    line-height:1.4!important;
    color:    #7E3517!important;
    }


    #content a{
    color:    #7E3517!important;
    }

    #content h1, #content h1 a{
      color: #347C2C !important;
    }

    .box h1, .smallbox h1, .widenobox h1{
    color: #FFF8C6 !important;
    }

    #content, #wrapper{
    background:    #FFE87C !important;
    }

    .box {
    background: none repeat scroll 0 0 #FAF8CC;
    border: 1px solid #7E3517;
    margin-bottom: 10px;
    min-height: 70px;
    padding-bottom: 10px;
    padding-left: 10px;
    padding-right: 10px;

    }
    }
