css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("thepiratebay.org") {

body,a,input {outline:medium none!important}

body,input[type="text"],#browseContainer {color:#555 !important;
font-family: Trebuchet MS !important;
font-size:13px !important

}

#categoriesTable {margin-top:0 !important}
h1 a:hover {border:none!important}
div.ad, #topleft {display:none !important}
a[href="http://thepiratebaytoolbar.ourtoolbar.com/"], #footer, #fbanners, div[id*="banner"], iframe,  div#sky-banner{
display:none !important;
}

a[title="Download this torrent"] {
font: bold 16px verdana !important;
}

#main-content {
margin:0 !important;
}

#detailsouterframe {
width:100% !important;
}

h2 {background-color:#666!important;color:white !important;font-size:12px !important;padding:5px !important;border:none !important;padding:5px 15px !important;overflow:hidden!important}
h2 span {font-size:13px !important;}

h2 a {color:white!important}
h2 a:hover {text-decoration:underline!important}
h2 + br {display:none}

#sky-right,#open-software,#topright {display:none !important}

a {color:#666 !important; border-bottom: 0px solid #555!important}
a:hover {border-bottom: 1px solid #555!important}
td[class="smalltext"], td[width="150"] {display:none}

input[type="text"] {
-moz-border-radius:3px !important;
height:20px !important;
font-size:13px;
}

.download a {text-transform:none!important}

table#searchResult {margin-bottom:20px!important}
#searchResult th:first-child, #searchResult tr td:first-child {padding-left:15px !important}
#searchResult th:last-child, #searchResult tr td:last-child {padding-right:15px !important}
table#searchResult tbody tr {background-color:#fafafa!important}
table#searchResult tbody tr:hover {
-moz-background-clip:border;
-moz-background-inline-policy:continuous;
-moz-background-origin:padding;
background:#eaeaea none repeat scroll 0 0!important;
}

#SearchResults {font-size:12px}
table#searchResult a {border:none !important}

#main-content table#searchResult tbody tr:last-child {color:red!important}

table#searchResult th, table#searchResult td {
border:none !important;font-size:13px!important
}

#searchResult tr td, #searchResult tr th {border-bottom:1px solid #ddd!important;padding:6px 5px !important;line-height:20px!important}
#searchResult tr th {background-color:#888!important;color:white !important}
#searchResult tr th a {color:white !important}

tbody {position:static!important}
#content {margin:0 !important;margin-top:0 !important;position:static!important}

#browseContainer dt, #browseContainer dd  {font-size:13px !important}
#categoriesTable {width:100% !important}
#q input.submitbutton {
margin-left:162px!important
}

input[type="submit"] {
margin-top:5px !important;
padding:2px 5px !important;
outline:none !important;
color:#555;
border:1px solid red
}
#foot {font-size:13px!important;margin-top:50px !important;line-height:22px!important}

#q .inputbox {padding:5px !important;vertical-align:top !important;margin-left:0 !important}
abbr {border:none !important;display:block !important;text-align:right !important}


a[title="Order by Uploaded"] + a[title="Order by Size"] {display:inline!important;text-align:right!important}
th a[title="Order by Size"] {display:block!important;text-align:right!important}
.vertTh center {text-align:left!important}

.vertTh a , .detName .detLink {font-size:13px!important }
.detDesc {font-size:12px;color:#888!important}

select#category {margin-left:10px !important;font-size:13px !important;font-family:Trebuchet MS;color:#666 !important;
position:relative!important;
top:-32px!important;
left:-26px;
height:26px!important
}


#details a[href="http://radiobar.toolbarhome.com/download.aspx"] {display:none}
#main-content {position:static!important}

#detailsouterframe #detailsframe #title {background-color:#888 !important;padding:10px 15px !important;font-size:20px!important;color:white !important;letter-spacing:0 !important;font-family:Calibri, Arial !important}

#details {background-color:#f5f5f5 !important;padding:0px 15px 15px 15px!important}

#details div.download {
/*position:absolute;
top:60px !important;*/
border:1px solid #dadada!important;
padding:10px;
-moz-border-radius:6px!important;
background-color:#ececec}

#details div#rssAdd1 {position:absolute;top:-1px !important;border:1px solid #dadada!important;padding:10px;-moz-border-radius:6px!important;background-color:#ececec;left:350px;width:260px;text-align:center}

#details div.download:hover, #details div#rssAdd:hover {background-color:#ddd}

#details div.download a {font-size:13px!important;background-position:0 3px!important;}
#details .col1 br, #details .col2 br {display:none}
#details .col1 {margin-top:25px!important}
#details .col1, #details .col2 {line-height:23px !important;}
#details .col1,#details .col2 {width:60%!important}
#details .col2 {margin-bottom:30px!important}
#details .col2,#details .col1 {clear:both!important}
#details dt {width:170px!important;text-align:right!important;border-bottom:1px solid #ddd!important}
#details dd {text-align:left!important;border-bottom:1px solid #ddd!important}
#details .torpicture {position:relative!important;top:-20px;float:right!important;margin:0!important}
#details .torpicture img {width:200px}


#details dl {margin:0!important}

#details .nfo {
width:100%!important;
padding:0!important;
border:1px solid #ddd!important;
background-color:#fafafa!important;
margin:0!important;
margin-top:20px !important
}
#details .nfo pre {
font-family:Consolas, Courier New!important;
padding:15px!important;
line-height:15px!important
}


#details + .nfo + div.download {display:none!important}

.spons-link {display:none}


#commentsheader h4 {
background-color:#ddd!important;
padding:10px 15px!important;
color:#666!important;


}
#comments div {
padding:10px 15px!important;
border:1px solid #eee!important;
margin:10px 0 !important;
background-color:white!important
}

#comments .comment a {color:#2FA7BF!important;border-bottom:1px dashed #2FA7BF!important}

#comments .comment a:hover {color:#237D8F!important;border-bottom:1px dashed #237D8F!important}

p.byline {margin:0!important;margin-bottom:5px!important;font-size:13px!important}
#comments .comment {margin:0!important;padding:0!important;border:none!important;color:#999!important;font-size:13px!important;font-style:italic!important;line-height:19px!important}

#comments .comment br {display:block;margin:3px!important;padding:0!important;height:0!important}

div#main-content + div[align="center"] a:hover, div.browse-coms a:hover, div.browse-coms strong, #sear1chResult tbody tr:last-child td a:hover {
background-color:#eee
}

#searchRe1sult tbody tr:last-child td {border-bottom:none!important;padding-top:20px!important}
div#main-content + div[align="center"] a, #search1Result tbody tr:last-child td a ,div.browse-coms a, div.browse-coms strong {
display:inline-block!important;
padding:2px!important;
border:1px solid #ddd!important;
width:12px!important;
font-size:8px;
height:18px;
text-decoration:none!important

}





}
