css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.sparvar.org") {

#content-wrap {
border: solid 1px #fff!important;
-moz-box-shadow: 0px 3px 8px #858585!important;
  -webkit-box-shadow: 0px 3px 8px #858585!important;
-moz-border-radius: 1em!important;
  -webkit-border-radius: 1em!important; }

#nav ul li a {
color: #757575!important;
-moz-transform: scale(1.1)!important;
  -webkit-transform: scale(1.1)!important; }

#nav ul li a:hover {
color: #5c5c5c!important;
-moz-transform: scale(1.2)!important;
  -webkit-transform: scale(1.2)!important; }

.statsbar, #searchinput, .form {
border: solid 1px #d8d8d8!important;
-moz-border-radius: 0.5em!important;
  -webkit-border-radius: 0.5em!important; }

input, p.showhide span { border: solid 1px #d8d8d8!important; }

.khakiform { 
-moz-border-radius: 0.5em!important; 
  -webkit-border-radius: 0.5em!important; }

.contentframe {
border: solid 1px #d8d8d8!important;
-moz-border-radius-topright: 0.5em!important;
  -webkit-border-radius-topright: 0.5em!important;
-moz-border-radius-bottomright: 0.5em!important;
  -webkit-border-radius-bottomright: 0.5em!important;
-moz-border-radius-bottomleft: 0.5em!important;
  -webkit-border-radius-bottomleft: 0.5em!important;
-moz-border-radius-topleft: 0.5em!important;
  -webkit-border-radius-topleft: 0.5em!important; }
  
.contenttext span {
background-color:#e0e7ef!important;
border: solid 1px #d8d8d8!important;
 margin-left: 10px!important;
 padding-bottom: 3px!important;
-moz-border-radius-topright: 0.5em!important;
  -webkit-border-radius-topright: 0.5em!important;
-moz-border-radius-topleft: 0.5em!important;
  -webkit-border-radius-topleft: 0.5em!important; }

.colhead {
background-color: #e0e7ef!important;
-moz-border-radius-topright: 0.5em!important;
  -webkit-border-radius-topright: 0.5em!important;
-moz-border-radius-topleft: 0.5em!important;
  -webkit-border-radius-topleft: 0.5em!important; }

center table tr td a img, #details-poster img { border: 1px solid #fff!important; }

/* Popup poster pÃ¥ startsidan */
center table tr td a img:hover {
-moz-transform: scale(1.5)!important;
  -webkit-transform: scale(1.5)!important;
-moz-box-shadow: 4px 4px 8px #505050!important;
  -webkit-box-shadow: 4px 4px 8px #505050!important; }


/* Popup poster i Spaningstornet */
.contentframe form table tbody tr.bg td img:hover {
-webkit-transform: scale(1.5)!important;
  -moz-transform: scale(1.5)!important;
-webkit-box-shadow: 4px 4px 8px #505050!important;
  -moz-box-shadow: 4px 4px 8px #505050!important; }

.contentframe form table tbody tr.bg2 td img:hover {
-webkit-transform: scale(1.5)!important;
  -moz-transform: scale(1.5)!important;
-webkit-box-shadow: 4px 4px 8px #505050!important;
  -moz-box-shadow: 4px 4px 8px #505050!important; }


/* Popup poster i details */
#details-poster img:hover {
-webkit-transform: scale(1.5)!important;
  -moz-transform: scale(1.5)!important;
-webkit-box-shadow: 4px 4px 8px #505050!important;
  -moz-box-shadow: 4px 4px 8px #505050!important;
z-index: 1!important; }


/* Animerade ikoner */
.statsright a img:hover {
-webkit-transform: scale(1.1) rotate(-30deg)!important;
  -moz-transform: scale(1.1) rotate(-30deg)!important; }

}
