css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("myspace.com") {

body, body * {
  background: #fff !important;
  background-image: none !important;
  color: #000 !important;
}

.googleLogo {
  margin: 0px !important;
  padding: 2px !important;
  float: left !important;
  background-color: #003399 !important;
}


body, div, p, strong, td, .text, .blacktext10, .blacktext12, 
a.searchlinkSmall, a.searchlinkSmall:link, a.searchlinkSmall:visited {
  font-family: sans-serif !important;
  font-size: 10pt !important;
  border: none !important;
}

table table table table, table table table, div table table, table.cols, div.mar5, table#bulletins {
  border-color: #aaa !important;
  border-style: solid !important;
  border-width: 1px !important;
}

table#bulletins th {
  font-weight: bold !important;
  border-color: #aaa !important;
  border-style: solid !important;
  border-width: 1px 0px 1px 0px !important;
}

table#bulletins td {
  border-width: 1px 0px 1px 0px !important;
  border-color: #aaa !important;
  border-style: solid !important;
  padding: 2px !important;
}

table#bulletins td, table#bulletins td a {
  font-size: 8pt !important;
}


body * a {
  font-family: sans-serif !important;
  font-weight: normal !important;
  font-size: 10pt !important;
  text-decoration: underline !important;
  color: #33a !important;
}

body * a:visited {
  color: #505 !important;
}

body * a:active {
  color: #f00 !important;
}

body * a:hover {
  color: #f00 !important;
}


object, embed, iframe {
  display:none !important;
  visibility: collapse !important;
}

img {
  max-width: 500px !important;
}

input {
  z-index: 1000 !important;
}

.contactTable table, table.contactTable td {
  background-image: none !important;
  background-color: #fff !important;
}

.contactTable a img {
  visibility: visible !important;
}

.contactTable .text {
  font-size: 10pt;
}

table table table table, table table table tbody tr td {
  background-color: #ccc;
  border: none;
}

.friendSpace * td {
  font-size: 8pt !important;
  border: none !important;
}

.friendSpace * a {
  font-size: 8pt !important;
}

.friendsComments * {
  background-color: #ddd !important;
}


body * {
  -moz-opacity:1 !important;
  opacity:1 !important; 
}


#header {
  height: auto !important;
}


.text {
  font-size: 10pt !important;
  font-family: sans-serif !important;
  color: black !important;
}

.lastBlogEntry {
  font-family: sans-serif;
  font-size: 10pt;
}

