css:
 @-moz-document 
  url(http://www.google.com/),
  url(http://www.google.co.uk/),
  url(http://www.google.ca/),
  url(http://www.google.se/),
  url(http://www.google.si/),
  url-prefix("http://www.google"), url-prefix("https://www.google") {

.mitem.msel { color: black !important; font-weight: bold; }
li.tbos { color: black !important; }

.micon { opacity: 0.4 !important; }
#logo img, #leftnav { opacity: 0.5 !important; }
a#logo { color: transparent; }

div.lsbb {
 background: none !important;
 border: none !important;
}
div.lsbb .lsb {
 background-color: lightgray !important;
 border-radius: 3px;
}

#resultStats { font-weight: bold; font-size: 13px }
#resultStats NOBR { font-weight: normal; }

}
