css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("episteme.arstechnica.com") {

a, a span {
color:#003399 !important;
text-decoration:none !important;
}

table.ev_container td, table.ev_container_bottom td, table.ev_container th {
background-color:#fff !important;
color:#000 !important;
border-top-width:0px !important;
}

#ev_portal_title_graphic_div,#ev_page_intro{
display:none !important;
}
table.ev_container{
 border-left-width:0px !important;
display:none !important;
}
td.eve_tab{
 background-color:#333 !important;
border-right-color:#fff !important;
border-bottom-color:#000 !important;
border:none !important;
}
div.ev_tab a{
 color:#fff !important;
}
div.ev_tab{
border-left-color:#e0e0e0 !important;
border-top-color:#e0e0e0 !important;
border-top:none !important;
}

.ip-user-type-mem{
 color:#003399;
font-weight:normal !important;
}
table.ev_container td *, table.ev_container_bottom td *, table.ev_container td a, table.ev_container td a:visited, table.ev_container_bottom td a:visited {
 color:#666 !important;
}
.ev_hopto_emphasized{
background-color:#ccc !important;
}
.ev_hopto_scroller {
background-color:#e0e0e0 !important;

}
.ev_msg_rowcolor2, table.ev_msg_rowcolor2 {
 background-color:#e3e3e3 !important;
}
td.eve_tab_hover{
 background-color:#333 !important;
border-right-color:#fff !important;
border-bottom-color:#000 !important;
border:none !important;
}

table.ev_container, table.ev_container_bottom, div.ev_container_bottom {
 border-top-width:1px !important;
 border-top-color:#ccc !important;
}
table.ev_container td *, table.ev_container_bottom td *, table.ev_container td a, table.ev_container td a:visited, table.ev_container_bottom td a:visited {
 color:#003399 !important;


}
div.ev_ubbx_tpc_alt {
border-top:0px !important;
}

}
