css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document 

  url-prefix(http://www.google.com.au/),
  url-prefix(http://www.google.com.au/webhp),
  url-prefix(http://www.google.com.au/search),
  url-prefix(http://www.google.com.au/#hl=),
  url-prefix(http://www.google.com.au/#q=),

  url(http://www.google.com/),
  url-prefix(http://www.google.com/webhp),
  url-prefix(http://www.google.com/search),
  url-prefix(http://www.google.com/#hl=),
  url-prefix(http://www.google.com/#q=),

  url(http://www.google.co.uk/),
  url-prefix(http://www.google.co.uk/webhp),
  url-prefix(http://www.google.co.uk/search),
  url-prefix(http://www.google.co.uk/#hl=),
  url-prefix(http://www.google.co.uk/#q=),

  url(http://www.google.ca/),
  url-prefix(http://www.google.ca/webhp),
  url-prefix(http://www.google.ca/search),
  url-prefix(http://www.google.ca/#hl=),
  url-prefix(http://www.google.ca/#q=)

{

/* everything in Segoe UI or Tahoma */
body, td, input, textarea, select, legend, button, option, div.s, font {
    font-family: "Segoe UI", Tahoma !important;
    }

/* specifies width of web search results column */
        div.s { max-width: 98% !important; }
        #ires { width: 683px !important; }


/* Paragraphs each containing a result of the search */
   .g {
	background: #f8f8f8 !important;
	border: solid 1px #e5e5e5 !important;
	-moz-border-radius: 3px !important;
	padding: 10px !important;
        width: 98% !important;
   }

/* "Did you mean:" Box */
   .ssp {
	background: #fff1f1 !important;
	border: solid 1px #efd7d7 !important;
	-moz-border-radius: 3px !important;
	padding: 10px !important;
        width: 85% !important;
   }

/* "Searches related to" Box */
    #brs {
	background: #f4fcf2 !important;
	border: solid 1px #e1f0de !important;
	-moz-border-radius: 3px !important;
	padding: 10px !important;
        width: 85% !important;
   }
    #center_col {
        margin-right: 185px ! important;
   }

/* "Videos for" Box */
   .videobox {
	background: #f4f1f7 !important;
	border: solid 1px #e6e2ec !important;
	-moz-border-radius: 3px !important;
	padding: 10px !important;
        width: 85% !important;
   }

/* "Images for" Box */
   #imagebox, #imagebox_bigimages {
	background: #f0f5f9 !important;
	border: solid 1px #d7e0ee !important;
	-moz-border-radius: 3px !important;
	padding: 10px !important;
        width: 85% !important;
   }

/* "Showing results/Search instead for" Box */
    .sp_cnt {
	background: #fffff1 !important;
	border: solid 1px #ececd6 !important;
	-moz-border-radius: 3px !important;
	padding: 10px !important;
        width: 85% !important;
   }

/* Link & Hover Settings */
  .g:hover {
    border: solid 1px #d2d2d2 ! important;
    background-color: #eeeeee ! important;
  } 
  .videobox:hover {
    border: solid 1px #d8caea ! important;
    background-color: #efe6f8 ! important; 
  }
  a:visited {
    color: #b3a216 ! important;
    text-decoration: line-through ! important;
  }
  a:link{
    text-decoration: none ! important;
  }
  a:hover {
    color: #c80101 ! important;
    text-decoration: underline ! important;
  }

}
