css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forocoches.com") {
/* vBulletin 3 CSS For Style 'ForoCoches' (styleid: 5) */
body
{
	background: #000000 !important;
	color: #FFFFFF !important;
	font: 20px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	margin: 5px 5px 10px 5px !important;
}
a:link, body_alink
{
	color: #FF3300 !important;
	text-decoration: none !important;
}
a:visited, body_avisited
{
	color: #FF3300 !important;
	text-decoration: none !important;
}
a:hover, a:active, body_ahover
{
	color: #00AAFF !important;
	text-decoration: underline !important;
}
.page
{
	background: #000000 !important;
	color: #FFFFFF !important;
}
td, th, p, li
{
	font: 18pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.tborder
{
	font: 18pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	background: #222222 !important;
	color: #00FFFF !important;
	border: 1px solid #a1a1a1 !important;
}
.tcat
{
	background: #000000 !important;
	color: #FFFF00 !important;
	font: bold 20pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.tcat a:link, .tcat_alink
{
	color: #000040 !important;
	text-decoration: none !important;
}
.tcat a:visited, .tcat_avisited
{
	color: #000040 !important;
	text-decoration: none !important;
}
.tcat a:hover, .tcat a:active, .tcat_ahover
{
	color: #6600FF !important;
	text-decoration: underline !important;
}
.thead
{
	background: #000000 !important;
	color: #FFFFFF !important;
	font: bold 18pt tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.thead a:link, .thead_alink
{
	color: #FFFFFF !important;
}
.thead a:visited, .thead_avisited
{
	color: #FFFFFF !important;
}
.thead a:hover, .thead a:active, .thead_ahover
{
	font: 18pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	color: #330099 !important;
}
.tfoot
{
	font: 16pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	background: #00102C !important;
	color: #E0E0F6 !important;
}
.tfoot a:link, .tfoot_alink
{
	color: #E0E0F6 !important;
}
.tfoot a:visited, .tfoot_avisited
{
	color: #E0E0F6 !important;
}
.tfoot a:hover, .tfoot a:active, .tfoot_ahover
{
	color: #0066FF !important;
}
.alt1, .alt1Active
{
	font: 18pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	background: #1f1f1f !important;
	color: #FFFFFF !important;
}
.alt2, .alt2Active
{
	font: 18pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	background: #2e2e2e !important;
	color: #FFFFFF !important;
}
td.inlinemod
{
	background: #FFFFCC !important;
	color: #000000 !important;
}
.wysiwyg
{
	background: #F5F5FF !important;
	color: #000000 !important;
	font: 18pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
textarea, .bginput
{
	font: 20pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.button
{
	font: 18pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
select
{
	font: 18pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
option, optgroup
{
	font-size: 18pt !important;
	font-family: verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.smallfont
{
	font: 13px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.time
{
	color: #66FF7F !important;
}
.navbar
{
	font: 18pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.highlight
{
	color: #FF0000 !important;
	font-weight: bold !important;
}
.fjsel
{
	background: #3E5C92 !important;
	color: #E0E0F6 !important;
}
.fjdpth0
{
	background: #000000 !important;
	color: #FFFFFF !important;
}
.panel
{
	background: #141715 !important;
	color: #FFFFFF !important;
	padding: 10px !important;
	border: 2px outset !important;
}
.panelsurround
{
	background: #444444 !important;
	color: #FFFFFF !important;
}
legend
{
	color: #C2C2FF !important;
	font: 16pt tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
}
.vbmenu_control
{
	background: #222222 !important;
	color: #FFFFFF !important;
	font: bold 20pt tahoma, verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	padding: 3px 6px 3px 6px !important;
	white-space: nowrap !important;
}
.vbmenu_control a:link, .vbmenu_control_alink
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.vbmenu_control a:visited, .vbmenu_control_avisited
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.vbmenu_control a:hover, .vbmenu_control a:active, .vbmenu_control_ahover
{
	color: #FFFFFF !important;
	text-decoration: underline !important;
}
.vbmenu_popup
{
	background: #000000 !important;
	color: #FFFFFF !important;
	border: 1px solid #0B198C !important;
}
.vbmenu_option
{
	background: #111111 !important;
	color: #ffffff !important;
	font: 24pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	white-space: nowrap !important;
	cursor: pointer !important;
}
.vbmenu_option a:link, .vbmenu_option_alink
{
	color: #22229C !important;
	text-decoration: none !important;
}
.vbmenu_option a:visited, .vbmenu_option_avisited
{
	color: #22229C !important;
	text-decoration: none !important;
}
.vbmenu_option a:hover, .vbmenu_option a:active, .vbmenu_option_ahover
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.vbmenu_hilite
{
	background: #000000 !important;
	color: #FFFFFF !important;
	font: 15pt verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif !important;
	white-space: nowrap !important;
	cursor: pointer !important;
}
.vbmenu_hilite a:link, .vbmenu_hilite_alink
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.vbmenu_hilite a:visited, .vbmenu_hilite_avisited
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
.vbmenu_hilite a:hover, .vbmenu_hilite a:active, .vbmenu_hilite_ahover
{
	color: #FFFFFF !important;
	text-decoration: none !important;
}
/* ***** styling for 'big' usernames on postbit etc. ***** */
.bigusername { font-size: 16pt !important; }

/* ***** small padding on 'thead' elements ***** */
td.thead, div.thead { padding: 4px !important; }

/* ***** basic styles for multi-page nav elements */
.pagenav a { text-decoration: none !important; }
.pagenav td { padding: 2px 4px 2px 4px !important; }

/* ***** define margin and font-size for elements inside panels ***** */
.fieldset { margin-bottom: 6px !important; }
.fieldset, .fieldset td, .fieldset p, .fieldset li { font-size: 11px !important; }

/* ***** don't change the following ***** */
form { display: inline !important; }
label { cursor: default !important; }
.normal { font-weight: normal !important; }
.inlineimg { vertical-align: middle !important; }


.cajasprin {
	BORDER-RIGHT: #e5e5e5 1px solid !important; BORDER-TOP: #e5e5e5 1px solid !important; BORDER-LEFT: #e5e5e5 1px solid !important; BORDER-BOTTOM: #e5e5e5 1px solid
}
.texto {
	FONT-SIZE: 18pt !important; COLOR: black !important; FONT-FAMILY: Arial,Verdana,Helvetica
}
.texto A {
	FONT-WEIGHT: bold !important; COLOR: #cc3300 !important; TEXT-DECORATION: none
}
.texto A:hover {
	COLOR: #330099 !important; TEXT-DECORATION: underline
}

.cfield { FONT-SIZE: 14pt !important; COLOR: #000000 !important; FONT-FAMILY: Verdana, Tahoma, Arial, Helvetica !important; }
.cbutton { background: #000000 !important; font-size: 10pt !important; border-width: 1px !important; font-weight: bold !important; }

.cmini {	FONT-SIZE: 12pt !important; }
.signature { FONT-SIZE: 12pt !important; COLOR: #666666 !important; }
.cmega {	FONT-SIZE: 22pt !important; }
.cmega2 {	 FONT-SIZE: 18pt !important; font-weight: bold !important; }
.fc2010 {	FONT-WEIGHT: bold !important; FONT-SIZE: 22pt !important; COLOR: #CC0000 !important; FONT-FAMILY: geneva,arial,Verdana,Helvetica !important; }

img {
max-width: 1300px !important;
max-height: 1300px !important;
}
}
