css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://site.sports.betfair.com/betting/MarketView.do) {

/* Book % text in the market header */
td#MVHeaderBackPercentage span,
td#MVHeaderLayPercentage span  {
color: black !important;
}

/* The main back colour, in the market header, all the back buttons, and bet manager */
.MVHeaderBackHeader,
table#placeBetsTable tr.MVHeaderBackBoldedHeader,
table#placeBetsVerifyTable tr.MVHeaderBackBoldedHeader,
td[id*="MVBackButton"] {
 background-color: #08f !important;
}

/* The main lay colour, in the market header, all the back buttons, and bet manager */
.MVHeaderLayHeader,
table#placeBetsTable tr.MVHeaderLayBoldedHeader,
table#placeBetsVerifyTable tr.MVHeaderLayBoldedHeader,
td[id*="MVLayButton"] {
 background-color: #4b0 !important;
}

/* Hide the silks */
img[src*="/SilkColours/"] {
/* display: none !important; */
}

/* Hide trainer name */
td.MVRunnerInfo table tr td div span {
/* display: none !important; */
}

/* P+L so don't hide this! */
td.MVRunnerInfo table tr + tr {
/* display: none !important; */
}

/* Add a light background to the market */
div#MVContentWrapper,
div#MVHeaderWrapper {
background-color: rgb(214,229,230) !important;
}

/* Change the size of the market buttons */
table#MVContentPredictionsTable tr[id$="runner"] ,
table#MVContentPredictionsTable tr[id$="runner"] td {
height: 26px !important;
border-spacing: 0px 0px !important;
}

/* The lightest back colour, in the market viewe and bet manager */
table#MVContentPredictionsTable tr[id$="runner"] td.MVContent2nd3rd ,
table#placeBetsVerifyTable tr.BMBackBetBackground ,
table#placeBetsVerifyTable td.BMBackBetInfoBackground ,
table#placeBetsTable tr.BMBackBetBackground ,
table#placeBetsTable div.BMBackBetBackground,
table#placeBetsTable div.BMBackBetInfoBackground{
background-color: rgb(142,195,238) !important;
}

/* The medium back colour in the market view */
table#MVContentPredictionsTable tr[id$="runner"] td.MVContent2nd3rd+td  {
background-color: rgb(71,162,247) !important;
}

/* The medium lay colour in the market view */
table#MVContentPredictionsTable tr[id$="runner"] td.MVContent2nd3rd+td+td+td+td {
background-color: rgb(114,193,76)!important;
}

/* The lightest lay colour, in the market view and bet manager */
table#MVContentPredictionsTable tr[id$="runner"] td.MVContent2nd3rd+td+td+td+td+td ,
table#placeBetsVerifyTable tr.BMLayBetBackground ,
table#placeBetsVerifyTable td.BMLayBetInfoBackground ,
table#placeBetsTable tr.BMLayBetBackground ,
table#placeBetsTable div.BMLayBetBackground ,
table#placeBetsTable div.BMLayBetInfoBackground {
background-color: rgb(164,210,152) !important;
}

}
