css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("helpplz.info") {

body {background-color:#2C2C2C !important;}
.topBanner {background:none repeat scroll 0 0 #3D3D3D !important;}
.topBannerFontSpan {color:#FFA500 !important;}
a.removeTextShadow {color:#BBB !important;}
.alphabetLink a:link {color:#FFA500 !important;}
.navBanner a:link {color:#AAA !important;}
.navBanner a:visited {color:#777 !important;}
.navBanner a:hover {color:#EEE !important;}
.navBanner a:active {color:#FFA500 !important;}
.languageSpan a {color:#999 !important;}
.languageSpan a:hover {color:#FFA500 !important; background:none !important;}

.topBanner table tbody tr td[align="right"][style="background-color: rgb(255, 251, 174); width: 350px;"]{background-color:#3D3D3D !important;}
.topBannerSmallerFontSpan {color:#FFA500 !important;}
.topBannerSmallerFontSpan a {color:#FFBB00 !important;}

.addBanner {background:none repeat scroll 0 0 #484848 !important; border-bottom:1px solid #1E1E1E !important; border-top:1px solid #5B5B5B !important;}
.addBannerFontSpan, .ctrlBannerFontSpan, .navBannerFontSpan, .copyrightBannerFontSpan {color:#828282 !important;}
.copyrightBanner a:link {color:#AAA !important;}

.ctrlBanner {background:none repeat scroll 0 0 #363636 !important;
border-bottom:1px solid #1E1E1E !important; border-top:1px solid #505050 !important;}

.navBanner {background:none repeat scroll 0 0 #363636 !important;
border-bottom:1px solid #1E1E1E !important; border-top:1px solid #505050 !important;}
.navBanner .disabled {color:#595959 !important;}

.content {background:-moz-linear-gradient(top, #4F4F4F, #353535)!important; border-color:#676767 #1E1E1E #1E1E1E !important; border-right:1px solid #1E1E1E !important;}
.submit {color:#FFA500 !important; text-decoration:none !important;}
.content input, .addBanner .addBox, .limit, .search, .jumpBox {-moz-appearance: none !important; border: 1px solid #616161 !important; color: #888 !important; background-color: #2D2D2D !important; -moz-border-radius: 3px !important;}
.copyrightBanner {background:none repeat scroll 0 0 #1E1E1E !important; border-top:1px solid #333 !important;}

.ui-stars-star a, .ui-stars-cancel a {background:url(data:image/png;base64,) no-repeat scroll 0 0 transparent !important;}
.ui-stars-star a {background-position:0 -24px !important;}
.ui-stars-star-on a {background-position:0 -36px !important;}
.ui-stars-star-hover a {background-position:0 -48px !important;}
.ui-stars-cancel-hover a {background-position:0 -12px !important;}

.cellBtnRow {color:#777 !important; margin-right:12px !important; font-size:8pt !important;}
.copyBtn, .content a {color:#FFA500 !important;}
.cellRating {margin-top:5px !important;}
.cellName {margin-top:10px !important;}
}
