css:
@namespace url(http://www.w3.org/1999/xhtml);
 
@-moz-document url-prefix("http://www.google"){
 
/* hide foot links and search bar + center navigation bar */
/* masque les liens et la barre de recherche en bas de page + recentre la barre de navigation */
#foot #fll, #foot #bfl {display:none!important}

.vspib{right:250px!important;}
 
#foot #nav, #foot #navcnt {
display:block!important;
margin-bottom:15px!important;
margin-left:20px!important;
margin-right:264px!important;
}
#foot #nav {
padding:10px 0px 10px 0px!important;
margin:10px 0px 0px 0px!important;
}

/* change navbar colors */
/* modification des couleurs de la navbar */
#nav a.fl{
color:GoldenRod !important;
}
#nav a.fl:visited, .fl a:visited{
color:GoldenRod !important;
opacity: 0.7 !important;
}
#nav td.cur a.fl, #nav .cur {
color:FireBrick !important;
}
 
/* navbar : "O" instead of "o" on hover and for current page */
/* navbar : "O" a la place de "o" au survol et pour la page courante */
a.fl span.csb {
background:url("http://www.google.com/images/experiments/nav_logo78.png") no-repeat scroll 0 0 transparent!important;
background-position:-50px -42px!important;
width:20px!important;
height:30px!important;
margin-bottom:5px!important;
margin-left:3px!important;
margin-right:2px!important;
}
a.fl:hover span.csb {
background:url("http://www.google.com/images/experiments/nav_logo78.png") no-repeat scroll 0 0 transparent!important;
background-position:-64px -52px!important;
width:25px!important;
height:30px!important;
margin-bottom:8px!important;
margin-top:-2px!important;
margin-left:0px!important;
margin-right:0px!important;
background-size: 850% 1250%!important;
}
#nav td.cur .csb, .cur .csb {
background:url("http://www.google.com/images/experiments/nav_logo78.png") no-repeat scroll 0 0 transparent!important;
background-position:-37px -52px!important;
width:25px!important;
height:30px!important;
margin-bottom:10px!important;
margin-top:-2px!important;
margin-left:1px!important;
margin-right:1px!important;
background-size: 850% 1250%!important;
}

#pnprev .csb.ch, tr:first-child td.navend.csb {
background:url("http://www.google.com/images/experiments/nav_logo78.png") no-repeat scroll 0 0 transparent!important;
background-position:-00px -42px!important;
width:28px!important;
height:31px!important;
margin-bottom:5px!important;
margin-left:3px!important;
margin-right:1px!important;
}
#pnprev:hover .csb.ch {
background:url("http://www.google.com/images/experiments/nav_logo78.png") no-repeat scroll 0 0 transparent!important;
background-position:-0px -50px!important;
width:37px!important;
height:40px!important;
margin-top:-10px!important;
margin-bottom:7px!important;
margin-left:3px!important;
margin-right:-4px!important;
background-size: 570% 1000%!important;
}
#nav td.b a#pnprev{
width:72px!important;
}

#pnnext .csb.ch {
background:url("http://www.google.com/images/experiments/nav_logo78.png") no-repeat scroll 0 0 transparent!important;
background-position:-70px -40px!important;
width:50px!important;
height:40px!important;
margin-top:-3px!important;
margin-bottom:-3px!important;
margin-left:3px!important;
margin-right:1px!important;
}
#pnnext:hover .csb.ch {
background:url("http://www.google.com/images/experiments/nav_logo78.png") no-repeat scroll 0 0 transparent!important;
background-position:-88px -53px!important;
width:70px!important;
height:60px!important;
margin-top:-10px!important;
margin-bottom:-15px!important;
margin-left:3px!important;
margin-right:-4px!important;
background-size: 300% 700%!important;
}
#nav td.b a#pnnext{
width:72px!important;
}

/* navbar : font size enlargment on hover and underline removal on normal */
/* navbar : aggrandissement de la police au survol et retrait souligne en normal */
#nav a {
text-decoration:none!important;
}
#nav td.b a {
width:100px!important;
}
#nav td.b a.pn:hover {
text-decoration:underline!important;
width:100px!important;
font-size:1.1em!important;
}
#nav td.b a:hover {
text-decoration:underline!important;
width:100px!important;
font-size:1.1em!important;
}
#nav a.fl:hover {
font-size:1.3em!important;
}
 
/*remove ads*/
/*retrait de la pub*/
#rhs, #tads, #brs{ display: none !important; }

/* Move sidebar from left to right*/
/* deplacement de la leftbar vers la droite */
#leftnav{
margin-left:90% !important;
border-left: 1px solid rgb(211, 225, 249)!important;
}
#center_col {
margin-left: 5px !important;
}

.lhshdr {
    left: 90%!important;
}

.tsf-p {
    padding-left: 205px!important;
    padding-right: 180px!important;
}

.jhp #tsf {
    margin: 0 auto!important;
    max-width: 1181px!important;
}

#cnt {
    max-width: 1050px!important;
}

#vspb {
    left: 600px!important;
}

/* rounded button */
/* bouton arrondi */
#lst-ib, #misspell, #lst-xbtn, .lst{
border-radius : 10px 0px 0px 5px!important;
-moz-border-radius : 10px 0px 0px 5px!important;
-webkit-border-top-left-radius: 10px!important;
-webkit-border-top-right-radius: 0px!important;
-webkit-border-bottom-right-radius: 0px!important;
-webkit-border-bottom-left-radius: 5px!important;
}

.lst-td-xbtn{
border-radius : 0px 5px 10px 0px!important;
-moz-border-radius : 0px 5px 10px 0px!important;
-webkit-border-top-left-radius: 0px!important;
-webkit-border-top-right-radius: 5px!important;
-webkit-border-bottom-right-radius: 10px!important;
-webkit-border-bottom-left-radius: 0px!important;
}

.lsbb, #sftab, .lst-d, .lst-t{
border-radius : 10px 5px 10px 5px!important;
-moz-border-radius : 10px 5px 10px 5px!important;
-webkit-border-top-left-radius: 10px!important;
-webkit-border-top-right-radius: 5px!important;
-webkit-border-bottom-right-radius: 10px!important;
-webkit-border-bottom-left-radius: 5px!important;
}

.lsbb
{
opacity:0.7!important;
}
.lsbb:hover, .lsb:hover
{
opacity:1.0!important;
color:#0055FF !important;
}

.jhp input[type="submit"] {
border-radius : 10px 5px 10px 5px!important;
-moz-border-radius : 10px 5px 10px 5px!important;
-webkit-border-top-left-radius: 10px!important;
-webkit-border-top-right-radius: 5px!important;
-webkit-border-bottom-right-radius: 10px!important;
-webkit-border-bottom-left-radius: 5px!important;
}

/* highlight result links */
/* liens de reponse en surbrillance */
.l:hover, .fc a:hover, .brs_col a:hover, .r a:hover{
color:#0055FF!important;
}
.l:visited:hover, .fc a:visited:hover, .brs_col a:visited:hover, .r a:visited:hover{
color:#AA00AA!important;
}
#foot{
background: none repeat scroll 0 0 #F5F5F5!important;
width:100%!important;
height:100px!important;
margin:0!important;
border-top: 1px solid #E5E5E5!important;
}
}
