css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.pro-rock.info") {

body, table, tr, td, th, table.ipbtable, tr.ipbtable, td.ipbtable, .postcolor {
  background: #ffffff !important;
  color: black !important;
}

input, textarea, select {
  background: #f8f8f8 !important;
  color: black !important;
}

a:link, a:visited {
  color: #202080 !important;
}

.forumdesc, .forumdesc a:link, .forumdesc a:visited, .forumdesc a:active, .desc {
  color: grey !important;
}

.pagelink, .pagelinklast, .pagecurrent, .minipagelink, .minipagelinklast {
  background: white !important;
  border: none !important;
  color: black !important;
}

#userlinks, #userlinksguest, #navstrip, .copyright, #submenu, .barc, .formtable td.formtitle, .formsubtitle, .popmenubutton, .toplinks span, #ucpmenu, #ucpcontent, .plainborder, .tablefill, .tablepad, .calday, .calweekday, #redirectwrap p, #redirectwrap h4, #redirectwrap p.redirectfoot {
  background: white !important;
  color: black !important;
}

.mya-content, .tabon, .taboff, .mya-back, .borderwrap h3, .maintitle, .maintitlecollapse, .borderwrap p, .row2, .row1, .calmonths {
  background: #f8f8f8 !important;
  color: black !important;
}

.popupmenu, .popupmenu-category, .popupmenu-item, .bar, .barb, .barc {
  background: #f8f8f8 !important;
  color: black !important;
  border-color: #eee !important;
}

.row1, .row2 {
  border-top-color: #eee !important;
  border-right-color: #eee !important;
  border-bottom-color: #eee !important;
  border-left-color: #eee !important;
}

.quotemain, .quotetop {
  background: #f8f8f8 !important;
  color: #888 !important;
}

#ipbwrapper {
  width: 100% !important;
  color: black !important;
}

.ruleswrap {
  background: #eee !important;
}

.catend {
  display: none !important;
}

#logostrip {
  height: 100px !important;
  background-image: none !important;
  background-color: #666 !important;
}

}
